INSERT INTO `sach` (`id`, `tensach`, `tacgia`, `anh`, `noidung`) VALUES
(2, 'Anh Hùng Ca 1 Chương 1', 'Lê Tuấn', 'anhungca1.jpg', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">THI&Ecirc;N NGH&Ecirc; KH&Uacute;C</span></span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Chương 1</span></span></span></em><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">NH&Acirc;N DUY&Ecirc;N</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Giữa trời đất m&ecirc;nh mang, tuyết rơi lặng lẽ. Đằng sau ngọn n&uacute;i bị bao phủ bởi một m&agrave;u trắng to&aacute;t, &aacute;nh lửa bừng l&ecirc;n ngợp trời. Tiếng lửa ch&aacute;y l&ugrave;ng b&ugrave;ng, tiếng người g&agrave;o th&eacute;t ầm ĩ, tiếng bước ch&acirc;n hỗn loạn, tất cả c&ugrave;ng đập tan sự y&ecirc;n b&igrave;nh v&agrave; tĩnh lặng của đ&ecirc;m đ&ocirc;ng lất phất tuyết rơi.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Chợt c&oacute; một b&oacute;ng h&igrave;nh qu&aacute;i dị lao vọt l&ecirc;n giữa ngọn lửa dữ dội ấy. Đ&oacute; l&agrave; một con rắn to lớn kh&ocirc;ng b&uacute;t n&agrave;o tả xiết, sau lưng c&oacute; hai cặp c&aacute;nh khổng lồ. Khi hai cặp c&aacute;nh ấy dang rộng, cả bầu trời đ&ecirc;m chừng như bị che khuất. N&oacute; giận dữ xoay chuyển th&acirc;n h&igrave;nh, ngẩng đầu nh&igrave;n trời, r&iacute;t l&ecirc;n một tiếng đầy th&ecirc; lương. Những ngọn lửa dữ dội lập tức b&ugrave;ng l&ecirc;n, cao tới cả trượng, v&ocirc; số đốm tro t&agrave;n bị lửa n&oacute;ng đẩy đi xa t&iacute;t, nhanh ch&oacute;ng h&ograve;a lẫn với những b&ocirc;ng tuyết đang bay khắp trời.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đối mặt với con y&ecirc;u th&uacute; đ&aacute;ng sợ, những v&otilde; sĩ đang tụ tập tr&ecirc;n n&uacute;i vội v&agrave;ng l&ugrave;i lại. Một số người kh&ocirc;ng kịp n&eacute; tr&aacute;nh, lập tức bị ngọn lửa nuốt chửng, chỉ c&ograve;n biết đau đớn g&agrave;o th&eacute;t, m&ugrave;i thịt ch&aacute;y kh&eacute;t nhanh ch&oacute;ng lan tỏa khắp xung quanh. Nh&igrave;n thấy t&igrave;nh cảnh n&agrave;y, một nh&oacute;m đạo sĩ đầu đội mũ cao, m&igrave;nh kho&aacute;c đạo b&agrave;o, tay cầm trường kiếm đồng loạt bước l&ecirc;n ph&iacute;a trước. Người dẫn đầu th&acirc;n h&igrave;nh cao gầy, mặt mũi tuấn t&uacute;, mặc một chiếc &aacute;o b&agrave;o m&agrave;u t&iacute;m c&oacute; th&ecirc;u h&igrave;nh th&aacute;i cực lưỡng nghi. Y một tay cầm kiếm, một tay cầm b&ugrave;a, cất giọng sang sảng: &ldquo;Kiếm trận, khởi!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&Aacute;nh t&iacute;m rực rỡ chiếu rọi l&ecirc;n tận trời cao, hai mươi tư đạo kiếm quang h&igrave;nh th&agrave;nh n&ecirc;n một trận ph&aacute;p phỏng theo những v&igrave; sao tr&ecirc;n trời, tức th&igrave; chặn đứng mọi đường tiến lui của con y&ecirc;u th&uacute; Minh X&agrave; kia. Kiếm quang chuyển động, kiếm kh&iacute; ngợp trời, dần dần h&igrave;nh th&agrave;nh n&ecirc;n một bức đồ h&igrave;nh m&agrave;u t&iacute;m giữa bầu trời đ&ecirc;m. Sau một tiếng qu&aacute;t lớn của người cầm đầu, hai mươi tư thanh trường kiếm ng&acirc;n vang kh&ocirc;ng dứt giữa kh&ocirc;ng trung, sau đ&oacute; đột nhi&ecirc;n xoay tr&ograve;n v&agrave; đồng loạt lao thẳng về ph&iacute;a Minh X&agrave;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Th&acirc;n rắn, c&aacute;nh rắn đều bị trường kiếm đ&acirc;m xuy&ecirc;n, những d&ograve;ng dịch thể m&agrave;u trắng bạc tựa thủy ng&acirc;n cuồn cuộn chảy ra dọc theo th&acirc;n rắn. Con y&ecirc;u th&uacute; Minh X&agrave; r&uacute; l&ecirc;n th&ecirc; thảm, đ&acirc;m đầu xuống đất, lao thẳng về ph&iacute;a đ&aacute;m người b&ecirc;n dưới. Một đạo sĩ tu vi c&ograve;n non k&eacute;m bị đu&ocirc;i rắn quất bay đi. Trong khoảnh khắc y&ecirc;u th&uacute; Minh X&agrave; muốn d&ugrave;ng c&aacute;i thế từ tr&ecirc;n cao lao xuống m&agrave; đồng quy vu tận với mọi người, chỉ thấy đạo sĩ &aacute;o t&iacute;m đột nhi&ecirc;n tung m&igrave;nh nhảy vọt l&ecirc;n, cưỡi gi&oacute;, gằn giọng qu&aacute;t: &ldquo;Ph&aacute;!&rdquo; L&uacute;c l&aacute; b&ugrave;a trong tay được n&eacute;m tới để phong tỏa động t&aacute;c của Minh X&agrave;, thanh trường kiếm của y đ&atilde; đ&acirc;m thẳng v&agrave;o giữa đầu con rắn.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tấm th&acirc;n khổng lồ của Minh X&agrave; nặng nề rơi xuống nền đất tuyết, kh&ocirc;ng c&ograve;n ch&uacute;t động tĩnh n&agrave;o nữa, xem chừng đ&atilde; chết hẳn rồi.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Ch&iacute;nh v&agrave;o l&uacute;c n&agrave;y, một v&otilde; sĩ ở m&eacute; đ&ocirc;ng đột nhi&ecirc;n h&ocirc; lớn: &ldquo;C&ograve;n một con nữa!&rdquo; Đạo sĩ &aacute;o t&iacute;m lập tức dẫn theo c&aacute;c m&ocirc;n nh&acirc;n vội v&atilde; chạy về ph&iacute;a đ&ocirc;ng ngọn n&uacute;i. Tiểu đạo sĩ &aacute;o vải bị Minh X&agrave; quất bay l&uacute;c n&agrave;y c&ograve;n đang cố gắng b&ograve; dậy, kh&ocirc;ng kịp đi theo c&aacute;c đồng m&ocirc;n. Đột nhi&ecirc;n, y nh&igrave;n thấy chỗ thi thể con y&ecirc;u th&uacute; Minh X&agrave; kia vừa rơi xuống c&oacute; thứ g&igrave; đ&oacute; đang nh&uacute;c nh&iacute;ch.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiểu đạo sĩ cầm trường kiếm d&egrave; dặt đi về ph&iacute;a x&aacute;c rắn, chợt thấy một b&eacute; trai khoảng mười một, mười hai tuổi đang cố gắng b&ograve; ra từ b&ecirc;n dưới đu&ocirc;i rắn.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiểu đạo sĩ thở ph&agrave;o một hơi, bu&ocirc;ng trường kiếm rồi ngồi xuống trước mặt b&eacute; trai v&agrave; nhỏ giọng hỏi: &ldquo;N&agrave;y nh&oacute;c, em bị y&ecirc;u qu&aacute;i bắt đến đ&acirc;y ư? Nh&agrave; em ở đ&acirc;u, để ta đưa em về nh&agrave; nh&eacute;?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">B&eacute; trai n&agrave;y ngũ quan tuấn t&uacute;, tuy c&ograve;n chưa hết vẻ non nớt nhưng m&agrave;y rậm mắt to, tr&ecirc;n khu&ocirc;n mặt thấp tho&aacute;ng to&aacute;t ra kh&iacute; thế oai h&ugrave;ng. Đứa b&eacute; kh&ocirc;ng trả lời, chỉ lẳng lặng nh&igrave;n người trước mặt bằng cặp mắt long lanh đen l&aacute;y.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiểu đạo sĩ ngỡ rằng n&oacute; đang sợ h&atilde;i qu&aacute; độ, b&egrave;n an ủi: &ldquo;Nh&oacute;c con, em đừng sợ!&rdquo; Đồng thời đưa tay tới, định xoa đầu n&oacute; để tỏ &yacute; vỗ về. Nhưng b&agrave;n tay c&ograve;n chưa kịp chạm v&agrave;o đầu đối phương th&igrave; y chợt cảm thấy trước mắt hoa l&ecirc;n, thế rồi một lưỡi kiếm lạnh băng kề v&agrave;o cổ y. Chỉ thấy đứa b&eacute; đ&oacute; đang cầm thanh bảo kiếm c&ograve;n d&agrave;i hơn cả người n&oacute;, hờ hững nh&igrave;n y chằm chằm. Tiểu đạo sĩ thầm kinh h&atilde;i, khi nh&igrave;n lại th&igrave; thấy tr&ecirc;n khu&ocirc;n mặt non nớt nhưng lạnh l&ugrave;ng của đối phương c&oacute; một vệt nước m&agrave;u trắng bạc ở giữa tr&aacute;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Những b&ocirc;ng tuyết lạnh gi&aacute; của ng&agrave;y đ&ocirc;ng nhẹ nh&agrave;ng đậu xuống vai b&eacute; trai, n&oacute; chỉ cần khẽ hất c&aacute;nh tay l&agrave; đủ khiến g&atilde; đạo sĩ vừa b&agrave;y tỏ thiện &yacute; với m&igrave;nh đầu l&igrave;a khỏi cổ. Nhưng đối mặt với con người đang lộ r&otilde; vẻ sợ h&atilde;i n&agrave;y, n&oacute; bỗng cảm thấy hơi do dự. Đột nhi&ecirc;n, tiếng gi&oacute; lật phật vang l&ecirc;n, một l&aacute; b&ugrave;a m&agrave;u v&agrave;ng với những n&eacute;t vẽ bằng mực chu sa bay vọt đến v&agrave; đ&aacute;nh thẳng v&agrave;o lưng đứa b&eacute;, sau nh&aacute;y mắt đ&atilde; nổ tung. Th&igrave; ra vị đạo sĩ &aacute;o t&iacute;m đ&atilde; kịp quay trở lại. Sức nổ của l&aacute; b&ugrave;a lập tức hất bay b&eacute; trai l&ecirc;n cao, khiến n&oacute; phun ra một ngụm m&aacute;u m&agrave;u bạc. N&oacute; loạng choạng b&ograve; dậy, vội v&atilde; bỏ trốn v&agrave;o trong rừng. Đạo sĩ &aacute;o t&iacute;m tức tốc cầm kiếm đuổi theo, song lại bị tiểu đạo sĩ đưa tay ngăn cản. &ldquo;Đại sư huynh, n&oacute; chỉ l&agrave; một đứa b&eacute; th&ocirc;i m&agrave;!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Hoang đường!&rdquo; Đạo sĩ &aacute;o t&iacute;m phất tay &aacute;o, đẩy tiểu đạo sĩ ra. &ldquo;Một đứa b&eacute; c&aacute;i g&igrave; chứ, n&oacute; l&agrave; y&ecirc;u nghiệt! Nhổ cỏ kh&ocirc;ng nhổ tận gốc sẽ g&acirc;y hậu họa v&ocirc; c&ugrave;ng lớn!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Dứt lời, đạo sĩ &aacute;o t&iacute;m tung m&igrave;nh nhảy vọt l&ecirc;n cao, chuẩn bị đuổi theo y&ecirc;u nghiệt, nhưng c&ograve;n chưa kịp lao đi th&igrave; đ&atilde; bị tiểu đạo sĩ &ocirc;m chặt lấy ch&acirc;n. &ldquo;Sư huynh, n&oacute; chỉ l&agrave; một tiểu y&ecirc;u th&ocirc;i, chờ sau n&agrave;y n&oacute; g&acirc;y họa rồi h&atilde;y diệt trừ cũng chưa muộn.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Người &aacute;o t&iacute;m vung ch&acirc;n đ&aacute; bay đối phương, sau đ&oacute; lại t&aacute;t cho sư đệ của m&igrave;nh một c&aacute;i thật mạnh, gằn giọng chất vấn: &ldquo;Hay cho c&acirc;u &ldquo;Chờ sau n&agrave;y n&oacute; g&acirc;y họa rồi h&atilde;y diệt trừ cũng chưa muộn!&rdquo; Nếu trong thời gian đ&oacute; c&oacute; người mất mạng, m&oacute;n nợ mạng người n&agrave;y sẽ t&iacute;nh l&ecirc;n đầu ai đ&acirc;y?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiểu đạo sĩ đưa tay &ocirc;m mặt, bất gi&aacute;c sững người. Đạo sĩ &aacute;o t&iacute;m kh&ocirc;ng n&oacute;i g&igrave; th&ecirc;m, tung m&igrave;nh nhảy l&ecirc;n ngọn t&ugrave;ng, gần như l&agrave; cưỡi gi&oacute; lao đi, vội v&atilde; truy đuổi theo hướng m&agrave; b&oacute;ng d&aacute;ng đứa b&eacute; kia vừa mất dạng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Giữa v&ugrave;ng n&uacute;i non hiểm trở, một c&aacute;i b&oacute;ng m&agrave;u bạc vun v&uacute;t lao đi. Đ&oacute; l&agrave; một con Minh X&agrave; d&agrave;i chừng ba thước, c&aacute;nh của n&oacute; đ&atilde; tả tơi lắm rồi, b&ecirc;n tr&ecirc;n bị thủng một lỗ rất lớn. Trong qu&aacute; tr&igrave;nh bay, th&acirc;n thể n&oacute; kh&ocirc;ng ngừng nghi&ecirc;ng ngả, thỉnh thoảng c&ograve;n bị va v&agrave;o những gốc t&ugrave;ng rất lớn khiến cho tuyết tr&ecirc;n c&acirc;y r&agrave;o r&agrave;o rơi xuống.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiếng bước ch&acirc;n v&agrave; tiếng h&ograve; h&eacute;t dần dần bị bỏ xa, v&ugrave;ng rừng n&uacute;i quay trở lại vẻ tịch mịch ban đầu, nhưng luồng s&aacute;t kh&iacute; dữ dội kia vẫn lu&ocirc;n theo s&aacute;t ph&iacute;a sau. Trong l&uacute;c chạy trốn, miệng vết thương tr&ecirc;n c&aacute;nh Minh X&agrave; kh&ocirc;ng ngừng bị x&eacute; rộng ra, cuối c&ugrave;ng to&agrave;n th&acirc;n n&oacute; run l&ecirc;n lẩy bẩy, c&aacute;nh bị r&aacute;ch toạc, th&acirc;n thể va v&agrave;o một gốc t&ugrave;ng cổ thụ rồi rơi xuống đất, lại một lần nữa biến th&agrave;nh h&igrave;nh người. Sau c&uacute; va mạnh của n&oacute;, tuyết tr&ecirc;n c&acirc;y &agrave;o &agrave;o rơi xuống, v&ugrave;i lấp qu&aacute; nửa th&acirc;n thể n&oacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Sức lực to&agrave;n th&acirc;n kh&ocirc;ng ngừng tr&ocirc;i theo d&ograve;ng m&aacute;u m&agrave;u trắng bạc vẫn đang chảy ra ngo&agrave;i, th&acirc;n thể bị băng tuyết l&agrave;m cho cứng đờ, &yacute; thức của đứa b&eacute; dần trở n&ecirc;n mơ hồ. Trong cơn mơ m&agrave;ng, n&oacute; lo&aacute;ng tho&aacute;ng nh&igrave;n thấy cha gầm l&ecirc;n giận dữ giữa ngọn lửa dữ dội, nhưng cuối c&ugrave;ng vẫn bị v&ocirc; số lưỡi kiếm hạ s&aacute;t; mẹ n&oacute; mắt rơm rớm lệ, h&ocirc;n l&ecirc;n tr&aacute;n n&oacute; lần cuối để từ biệt, sau đ&oacute; h&oacute;a th&agrave;nh h&igrave;nh rắn lao ra khỏi động phủ...</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đứa b&eacute; nắm chặt b&agrave;n tay, cho d&ugrave; to&agrave;n th&acirc;n băng gi&aacute; nhưng trong l&ograve;ng vẫn ch&aacute;y bừng lửa giận, khiến &yacute; thức của n&oacute; bị thi&ecirc;u rụi ho&agrave;n to&agrave;n. L&uacute;c n&agrave;y, trong tư duy của n&oacute; chỉ c&oacute; duy nhất một chữ &ldquo;giết&rdquo;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Giết! Giết! Giết! Từ khi n&agrave;o y&ecirc;u linh trong trời đất lại rơi v&agrave;o cảnh bị lo&agrave;i người t&ugrave;y &yacute; giết ch&oacute;c vậy? Dưới gầm trời n&agrave;y, chỉ duy c&oacute; lo&agrave;i người l&agrave; kh&ocirc;ng n&ecirc;n tồn tại!</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nỗi căm hận ngợp đầy lồng ngực, n&oacute; cố gắng th&ograve; tay ra từ trong đống tuyết, đồng thời tập trung to&agrave;n bộ y&ecirc;u lực trong người v&agrave;o ấn đường, tức th&igrave; c&oacute; một luồng kh&iacute; n&oacute;ng tr&agrave;o ra từ tr&ecirc;n tr&aacute;n, trong cặp mắt đen l&aacute;y &aacute;nh l&ecirc;n những tia s&aacute;ng m&agrave;u bạc lạ thường. Đ&uacute;ng v&agrave;o khoảnh khắc đứa b&eacute; chuẩn bị hiện ra bản thể để liều một phen sống m&aacute;i với kẻ th&ugrave;, đột nhi&ecirc;n một đ&ocirc;i tay vừa ấm &aacute;p vừa mềm mại nắm lấy b&agrave;n tay lạnh băng của n&oacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;&Uacute;i, c&oacute; người bị v&ugrave;i trong tuyết n&agrave;y!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đ&oacute; l&agrave; một giọng trẻ con vừa trong trẻo vừa ngọt ng&agrave;o, v&agrave; rồi một gi&acirc;y sau đ&oacute;, đứa b&eacute; cảm thấy c&oacute; người đang k&eacute;o tay m&igrave;nh.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;D&ocirc; ta, d&ocirc; ta n&agrave;o!&rdquo; Người đ&oacute; dường như đang tự cổ vũ cho bản th&acirc;n, cố gắng k&eacute;o n&oacute; ra khỏi tuyết. K&eacute;o được một l&uacute;c, người đ&oacute; c&oacute; lẽ đ&atilde; thấm mệt, lại thấy chẳng c&oacute; ch&uacute;t t&aacute;c dụng n&agrave;o, b&egrave;n bỏ tay n&oacute; ra, chuyển sang bới tuyết.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đứa b&eacute; đột nhi&ecirc;n cảm thấy ph&iacute;a tr&ecirc;n đỉnh đầu m&igrave;nh nhẹ bẫng, b&egrave;n dốc hết sức lực ra tay với tốc độ cực nhanh. Trong khoảnh khắc chụp được cổ họng của người vừa xuất hiện, đ&ocirc;i mắt đen l&aacute;y của n&oacute; nh&igrave;n chằm chằm v&agrave;o đối phương...</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thứ lọt v&agrave;o mắt n&oacute; l&agrave; một đ&ocirc;i mắt long lanh s&aacute;ng rực như sao tr&ecirc;n trời, đồng thời dịu d&agrave;ng v&agrave; ấm &aacute;p như hổ ph&aacute;ch.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đ&oacute; l&agrave; một b&eacute; g&aacute;i chừng bảy, t&aacute;m tuổi, m&igrave;nh mặc &aacute;o b&ocirc;ng m&agrave;u xanh lục, t&oacute;c tr&ecirc;n đầu b&uacute;i th&agrave;nh hai b&uacute;i, khu&ocirc;n mặt nhỏ nhắn đỏ bừng, cặp mắt to tr&ograve;n s&aacute;ng lấp l&aacute;nh đang t&ograve; m&ograve; quan s&aacute;t đứa b&eacute; trai trước mặt.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Con người. Chỉ nh&igrave;n tho&aacute;ng qua, b&eacute; trai đ&atilde; x&aacute;c định được chủng tộc của đối phương, sự th&ugrave; hận đối với lo&agrave;i người khiến n&oacute; kh&ocirc;ng k&igrave;m được b&oacute;p chặt hai ng&oacute;n tay lại. Nhưng đ&uacute;ng v&agrave;o l&uacute;c n&agrave;y, b&eacute; g&aacute;i kia chợt giơ hai b&agrave;n tay nhỏ nhắn l&ecirc;n v&agrave; kh&eacute;p lại, ủ năm ng&oacute;n tay lạnh băng của n&oacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Đ&ocirc;ng cứng lại rồi, đ&ocirc;ng cứng lại rồi k&igrave;a! Rắn con ơi, ngươi đừng chết nh&eacute;!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Sự ấm &aacute;p truyền tới từ đầu ng&oacute;n tay khiến tr&aacute;i tim b&eacute; trai run l&ecirc;n, lời của b&eacute; g&aacute;i lại c&agrave;ng khiến n&oacute; kinh h&atilde;i. Tới l&uacute;c n&agrave;y, n&oacute; mới ph&aacute;t hiện do bị trọng thương tho&aacute;t lực, đ&ocirc;i ch&acirc;n của m&igrave;nh đ&atilde; biến trở về h&igrave;nh rắn, nhưng b&eacute; g&aacute;i kia vẫn chẳng tỏ vẻ sợ h&atilde;i, ra sức xoa tay n&oacute; để gi&uacute;p b&agrave;n tay n&oacute; ấm l&ecirc;n. Thỉnh thoảng c&ocirc; b&eacute; lại c&uacute;i đầu xuống h&agrave; hơi một ch&uacute;t, sau đ&oacute; hơi nghi&ecirc;ng đầu, t&ograve; m&ograve; hỏi: &ldquo;Rắn con ơi rắn con, trời lạnh thế n&agrave;y, sao ngươi kh&ocirc;ng về hang ngủ đ&ocirc;ng? C&oacute; phải ngươi bị đ&oacute;i n&ecirc;n chạy ra ngo&agrave;i kiếm mồi kh&ocirc;ng?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nghe nhắc đến từ &ldquo;hang&rdquo;, tr&aacute;i tim b&eacute; trai bỗng thắt lại, trong mắt bừng l&ecirc;n những tia s&aacute;ng bạc dữ dằn: Con người, đ&aacute;ng chết! Giết chết n&oacute;, lấy m&aacute;u thịt l&agrave;m thức ăn th&igrave; sẽ c&oacute; thể tăng y&ecirc;u lực!</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">N&oacute; đột nhi&ecirc;n giật tay ra khỏi hai b&agrave;n tay nhỏ nhắn mềm mại của đối phương, năm ng&oacute;n tay co lại th&agrave;nh trảo chụp thẳng về ph&iacute;a tr&aacute;i tim b&eacute; g&aacute;i.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Trong cặp mắt long lanh của b&eacute; g&aacute;i tức th&igrave; tho&aacute;ng qua một tia nghi hoặc. Đ&uacute;ng v&agrave;o khoảnh khắc năm ng&oacute;n tay của b&eacute; trai sắp chụp đến lồng ngực b&eacute; g&aacute;i, đột nhi&ecirc;n, miếng ngọc phỉ th&uacute;y được treo bằng chỉ đỏ tr&ecirc;n cổ b&eacute; g&aacute;i bừng l&ecirc;n những tia s&aacute;ng xanh biếc. Đầu ng&oacute;n tay đứa b&eacute; trai lập tức truyền tới cảm gi&aacute;c đau nh&oacute;i, th&acirc;n thể bị luồng s&aacute;ng xanh đẩy bắn ra ngo&agrave;i, đập v&agrave;o gốc c&acirc;y v&agrave; nặng nề rơi xuống đất.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Sao ngươi lại như vậy chứ?&rdquo; Nh&igrave;n đứa b&eacute; trai bị thương ng&atilde; dưới đất, b&eacute; g&aacute;i bất m&atilde;n trề m&ocirc;i, hậm hực n&oacute;i. &ldquo;Ta thấy ngươi gặp nạn n&ecirc;n mới gi&uacute;p đỡ, ngươi kh&ocirc;ng cần th&igrave; th&ocirc;i, cớ g&igrave; lại muốn hại ta?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Hừ, bớt n&oacute;i lời thừa th&atilde;i đi!&rdquo; B&eacute; trai nằm ng&atilde; tr&ecirc;n nền đất tuyết, một tay &ocirc;m ngực, hừ lạnh, n&oacute;i: &ldquo;Đừng c&oacute; l&agrave;m bộ l&agrave;m tịch, ai cần ngươi gi&uacute;p chứ? C&aacute;c ngươi giết chết cha mẹ ta, ph&aacute; nh&agrave; của ta, c&ograve;n n&oacute;i c&aacute;i g&igrave; m&agrave; ch&eacute;m hết y&ecirc;u ma trong thi&ecirc;n hạ nữa! C&aacute;c ngươi đều đ&aacute;ng chết, đ&aacute;ng chết!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Hả?&rdquo; B&eacute; g&aacute;i hơi sững người, sau khi cẩn thận quan s&aacute;t b&eacute; trai bằng đ&ocirc;i mắt m&agrave;u hổ ph&aacute;ch th&igrave; khẽ lẩm bẩm như đang n&oacute;i một m&igrave;nh: &ldquo;H&oacute;a ra ngươi bị người ta truy s&aacute;t. Sư phụ ta n&oacute;i, li&ecirc;n minh Trảm Y&ecirc;u đ&oacute; l&agrave; một li&ecirc;n minh hồ đồ, do một lũ người hồ đồ lập n&ecirc;n...&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">N&oacute;i tới đ&acirc;y, c&ocirc; b&eacute; chớp chớp mắt, đột nhi&ecirc;n đi l&ecirc;n ph&iacute;a trước, &aacute;p tay l&ecirc;n tr&aacute;n b&eacute; trai, ngăn kh&ocirc;ng cho d&ograve;ng m&aacute;u m&agrave;u bạc kia chảy ra, đồng thời nhỏ giọng an ủi: &ldquo;Đừng buồn, đừng buồn! Tiểu Tr&uacute;c v&agrave; sư phụ đều kh&ocirc;ng phải người hồ đồ, rắn con đừng sợ, đừng sợ m&agrave;!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;C&uacute;t đi! Ngươi bảo ai sợ hả?&rdquo; Đứa b&eacute; trai giận dữ qu&aacute;t lớn.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đ&uacute;ng v&agrave;o l&uacute;c n&agrave;y, luồng s&aacute;t kh&iacute; dữ dội kia c&agrave;ng l&uacute;c c&agrave;ng tới gần, đo&aacute;n chừng chẳng bao l&acirc;u sẽ tới. L&ograve;ng b&eacute; trai thầm động, định chụp lấy cổ b&eacute; g&aacute;i rồi bắt giữ l&agrave;m con tin, nhưng b&eacute; g&aacute;i bỗng chun mũi, hai tay đẩy mạnh, đột ngột đẩy n&oacute; v&agrave;o đống tuyết. B&eacute; trai kh&ocirc;ng ngờ đối phương đột nhi&ecirc;n ra tay, hơn nữa n&oacute; c&ograve;n đang trọng thương, lập tức bị v&ugrave;i trong tuyết. N&oacute; định tung người nhảy vọt l&ecirc;n, n&agrave;o ngờ b&eacute; g&aacute;i lại vung tay vỗ mạnh v&agrave;o gốc t&ugrave;ng cổ thụ gần đ&oacute;, tuyết tr&ecirc;n c&acirc;y tức th&igrave; rơi xuống r&agrave;o r&agrave;o, to&agrave;n th&acirc;n n&oacute; đều bị ch&ocirc;n s&acirc;u dưới tuyết.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Lo&agrave;i người quả nhi&ecirc;n l&agrave; hạng xảo tr&aacute; bỉ ổi, ngay đến một con ranh mấy tuổi m&agrave; đ&atilde; hiểm độc đến nhường n&agrave;y! B&eacute; trai thầm cắn chặt răng, chuẩn bị dốc sức nhảy l&ecirc;n, chợt cảm thấy đầu nặng trĩu, h&oacute;a ra b&eacute; g&aacute;i kia đ&atilde; ngồi lu&ocirc;n xuống đống tuyết rồi. Ngay sau đ&oacute;, s&aacute;t kh&iacute; tr&agrave;n tới, th&igrave; ra đạo sĩ &aacute;o t&iacute;m đ&atilde; đuổi tới nơi. &ldquo;Tiểu c&ocirc; nương, mau tr&aacute;nh ra!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">C&ocirc; b&eacute; hơi nghi&ecirc;ng đầu, ngước mắt l&ecirc;n quan s&aacute;t đối phương. Đạo sĩ &aacute;o t&iacute;m cầm kiếm trong tay, nhưng kh&ocirc;ng đưa ra trước mặt m&agrave; chỉ để ở sau lưng. C&ocirc; b&eacute; khẽ lắc đầu, cất giọng hỏi ngược lại: &ldquo;V&ugrave;ng n&uacute;i non hoang d&atilde; n&agrave;y đ&acirc;u phải l&agrave; nh&agrave; của huynh, lẽ n&agrave;o huynh bảo ta tr&aacute;nh ra th&igrave; ta nhất định phải tr&aacute;nh ư?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;B&ecirc;n dưới đống tuyết m&agrave; ngươi đang ngồi c&oacute; y&ecirc;u qu&aacute;i ẩn nấp, ngươi c&oacute; biết chăng?&rdquo; Đạo sĩ &aacute;o t&iacute;m trầm giọng n&oacute;i.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">B&eacute; g&aacute;i d&otilde;i mắt nh&igrave;n về ph&iacute;a ch&acirc;n trời, &aacute;nh mắt lập lờ bất định. &ldquo;Y&ecirc;u nghiệt c&aacute;i g&igrave; chứ, đ&acirc;y l&agrave; người tuyết ta đắp th&ocirc;i m&agrave;, tuy kh&ocirc;ng được dễ coi cho lắm nhưng cũng đ&acirc;u c&oacute; giống y&ecirc;u qu&aacute;i. Đại ca nhất định đ&atilde; nhầm lẫn rồi.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đạo sĩ &aacute;o t&iacute;m nh&iacute;u chặt đ&ocirc;i m&agrave;y, hừ lạnh bất m&atilde;n. &ldquo;Tiểu c&ocirc; nương, lẽ n&agrave;o cha mẹ ngươi chưa từng dạy ngươi rằng l&agrave;m người th&igrave; phải th&agrave;nh thực thủ t&iacute;n, kh&ocirc;ng được t&ugrave;y &yacute; n&oacute;i dối hay sao?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">B&eacute; g&aacute;i chớp chớp cặp mắt tr&ograve;n xoe s&aacute;ng rực, nh&igrave;n về ph&iacute;a đạo sĩ, nhếch kh&oacute;e m&ocirc;i cười n&oacute;i: &ldquo;Sư phụ ta quả c&oacute; dạy rằng l&agrave;m người phải th&agrave;nh thực thủ t&iacute;n, nhưng người cũng n&oacute;i rằng một đứa trẻ th&ocirc;ng minh cần phải biết xem x&eacute;t thời thế, nếu xuất ph&aacute;t từ thiện &yacute; th&igrave; c&oacute; n&oacute;i dối một ch&uacute;t cũng kh&ocirc;ng sao.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ăn n&oacute;i qu&agrave;ng xi&ecirc;n!&rdquo; Đạo sĩ &aacute;o t&iacute;m giận dữ qu&aacute;t. &ldquo;Tiểu c&ocirc; nương, con y&ecirc;u qu&aacute;i đ&oacute; giết người kh&ocirc;ng chớp mắt, lẽ n&agrave;o ngươi muốn trở th&agrave;nh thức ăn trong bụng n&oacute; ư?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ta đương nhi&ecirc;n kh&ocirc;ng muốn bị n&oacute; ăn thịt.&rdquo; C&ocirc; b&eacute; mỉm cười nh&igrave;n đạo sĩ, đột nhi&ecirc;n hỏi: &ldquo;Đại ca n&agrave;y, hổ cũng ăn thịt người, giả như huynh nh&igrave;n thấy một con hổ trong rừng, nhưng con hổ đ&oacute; đang ngủ, kh&ocirc;ng hề c&oacute; &yacute; định tấn c&ocirc;ng huynh, liệu huynh c&oacute; x&ocirc;ng l&ecirc;n đ&ograve;i giết bằng được con hổ đ&oacute; kh&ocirc;ng?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đạo sĩ &aacute;o t&iacute;m hơi sững người, hiển nhi&ecirc;n kh&ocirc;ng ngờ được đối phương lại c&oacute; c&acirc;u hỏi như vậy. Sau đ&oacute;, y đột nhi&ecirc;n phất mạnh tay &aacute;o, qu&aacute;t bảo: &ldquo;Hoang đường, m&atilde;nh th&uacute; tầm thường sao c&oacute; thể so s&aacute;nh với y&ecirc;u qu&aacute;i được!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;C&oacute; g&igrave; kh&aacute;c chứ?&rdquo; C&ocirc; b&eacute; nghi&ecirc;ng đầu n&oacute;i. &ldquo;Hổ ăn thịt người l&agrave; để cho no bụng. Y&ecirc;u qu&aacute;i ăn thịt người cũng l&agrave; để cho no bụng. Người ăn thịt tr&acirc;u b&ograve; lợn g&agrave; cũng l&agrave; để cho no bụng. Nếu y&ecirc;u qu&aacute;i ăn thịt người ngay trước mặt ta, ta đương nhi&ecirc;n sẽ gi&uacute;p con người đ&aacute;nh lại y&ecirc;u qu&aacute;i. Nhưng rắn con c&ograve;n nhỏ như vậy, chắc chắn l&agrave; chưa từng giết người. Nếu chỉ v&igrave; sau n&agrave;y n&oacute; c&oacute; thể giết người m&agrave; đ&ograve;i đuổi tận giết tuyệt, vậy tại sao đại ca kh&ocirc;ng đi giết hết to&agrave;n bộ hổ, b&aacute;o, gấu, voi tr&ecirc;n c&otilde;i đời n&agrave;y đi?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Kh&ocirc;ng đợi đạo sĩ phản b&aacute;c, b&eacute; g&aacute;i đ&atilde; giơ tay chỉ về ph&iacute;a rặng n&uacute;i phủ đầy tuyết trắng x&oacute;a ở đằng xa, n&oacute;i tiếp: &ldquo;V&ugrave;ng n&uacute;i Thanh Xuy&ecirc;n n&agrave;y vốn l&agrave; nơi cư ngụ của y&ecirc;u linh v&agrave; m&atilde;nh th&uacute;, con người tự dưng đi đến nơi đ&acirc;y th&igrave; phải c&oacute; sự chuẩn bị sẵn về t&acirc;m l&yacute; cho việc bị ăn thịt rồi. Cũng giống như một con hổ đi giữa nơi phố x&aacute; vậy, chắc chắn sẽ bị người ta đ&aacute;nh chết. Sư phụ ta n&oacute;i, như thế gọi l&agrave; &ldquo;nước s&ocirc;ng kh&ocirc;ng phạm nước giếng&rdquo;. Đại ca, ta biết m&igrave;nh kh&ocirc;ng gạt được huynh, rắn con quả thực đang ở nơi n&agrave;y, nhưng huynh h&atilde;y coi như l&agrave;m ph&uacute;c m&agrave; tha cho n&oacute; đi, được kh&ocirc;ng?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Lời của c&ocirc; b&eacute; len qua lớp tuyết d&agrave;y lọt v&agrave;o tai đứa b&eacute; trai, khiến l&ograve;ng n&oacute; tr&agrave;o d&acirc;ng s&oacute;ng gi&oacute;. Đạo l&yacute; về việc nước s&ocirc;ng kh&ocirc;ng phạm nước giếng mẹ cũng từng n&oacute;i với n&oacute;, nhưng cả gia đ&igrave;nh n&oacute; đều ở trong động phủ, hiếm khi n&agrave;o rời khỏi n&uacute;i Thanh Xuy&ecirc;n, n&oacute;i g&igrave; đến việc giết người, thế m&agrave; h&ocirc;m nay cha mẹ n&oacute; đ&atilde; gặp phải họa s&aacute;t th&acirc;n. Chỉ mới một khắc trước đ&acirc;y, n&oacute; c&ograve;n mong mỏi c&oacute; thể giết sạch lo&agrave;i người trong thi&ecirc;n hạ, vậy m&agrave; kh&ocirc;ng ngờ c&ocirc; b&eacute; yếu ớt n&oacute; kh&ocirc;ng hề quen biết kia lại gi&uacute;p n&oacute; chặn đứng kẻ th&ugrave;, c&ograve;n cất lời biện bạch v&agrave; cầu xin gi&uacute;p n&oacute;...</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đ&uacute;ng v&agrave;o l&uacute;c d&ograve;ng suy nghĩ của n&oacute; đang hỗn loạn, chợt nghe đạo sĩ &aacute;o t&iacute;m qu&aacute;t lớn: &ldquo;Tiểu c&ocirc; nương, ngươi một l&ograve;ng bảo vệ lo&agrave;i y&ecirc;u nghiệt, rốt cuộc l&agrave; c&oacute; &yacute; đồ g&igrave;?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Bớt n&oacute;i lời thừa đi, mau tr&aacute;nh sang một b&ecirc;n, bằng kh&ocirc;ng chớ tr&aacute;ch ta động thủ!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;&Uacute;i chao!&rdquo; B&eacute; g&aacute;i kinh h&atilde;i k&ecirc;u l&ecirc;n một tiếng, lớp tuyết ph&iacute;a tr&ecirc;n bỗng nhẹ bẫng, chắc l&agrave; c&ocirc; b&eacute; đ&atilde; bị k&eacute;o qua một b&ecirc;n rồi. B&eacute; trai bất gi&aacute;c cảm thấy lồng ngực n&oacute;ng bừng, ngợp đầy lửa giận, b&egrave;n vận hết y&ecirc;u lực to&agrave;n th&acirc;n, tung m&igrave;nh nhảy ra khỏi đống tuyết. Chỉ thấy c&aacute;nh tay c&ocirc; b&eacute; bị đạo sĩ giữ chặt, cả người kh&ocirc;ng ngừng gi&atilde;y giụa.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Bu&ocirc;ng c&ocirc; ấy ra!&rdquo; B&eacute; trai giận dữ qu&aacute;t lớn, năm ng&oacute;n tay quắp lại th&agrave;nh trảo, chụp thẳng về hướng mặt đạo sĩ. Đạo sĩ &aacute;o t&iacute;m lập tức vứt b&eacute; g&aacute;i sang một b&ecirc;n, sau đ&oacute; ngấm ngầm bắt quyết, thanh kiếm trong tay tức th&igrave; bừng l&ecirc;n một đạo kiếm quang m&agrave;u t&iacute;m s&aacute;ng l&ograve;a. Đ&uacute;ng v&agrave;o khoảnh khắc kiếm quang sắp đ&aacute;nh bay b&eacute; trai đi, b&eacute; g&aacute;i đột nhi&ecirc;n chạy tới b&ecirc;n cạnh đạo sĩ v&agrave; h&aacute; miệng cắn v&agrave;o c&aacute;nh tay y.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đạo sĩ bừng bừng nổi giận, gằn giọng qu&aacute;t: &ldquo;Đ&uacute;ng l&agrave; đồ tiểu quỷ kh&ocirc;ng biết ph&acirc;n biệt phải tr&aacute;i, chớ tr&aacute;ch ta ra tay v&ocirc; t&igrave;nh!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Dứt lời, đạo sĩ vung trường kiếm l&ecirc;n, ch&eacute;m về ph&iacute;a c&ocirc; b&eacute;. B&eacute; trai thấy vậy th&igrave; chẳng kịp suy nghĩ, lập tức tung người lao đến chụp lấy lưỡi kiếm lạnh băng, đồng thời đứng chắn trước người b&eacute; g&aacute;i. Lưỡi kiếm cứa r&aacute;ch l&ograve;ng b&agrave;n tay, một d&ograve;ng m&aacute;u bạc chảy tr&agrave;n, ph&aacute;p lực ẩn tr&ecirc;n lưỡi kiếm c&ograve;n tỏa ra đ&aacute;nh l&ecirc;n khắp người b&eacute; trai. Nhưng n&oacute; vẫn cắn chặt răng, ki&ecirc;n quyết kh&ocirc;ng nh&uacute;c nh&iacute;ch, một l&ograve;ng bảo vệ b&eacute; g&aacute;i sau lưng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Mang ơn biết b&aacute;o đ&aacute;p, coi như ngươi c&ograve;n c&oacute; ch&uacute;t lương t&acirc;m.&rdquo; Đạo sĩ &aacute;o t&iacute;m hừ lạnh, n&oacute;i: &ldquo;Đ&atilde; như vậy, ta sẽ cho ngươi được chết một c&aacute;ch gọn ghẽ!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Lời c&ograve;n chưa dứt, đạo sĩ đ&atilde; th&ograve; tay tr&aacute;i v&agrave;o trong vạt &aacute;o, lấy ra một tấm b&ugrave;a, đoạn qu&aacute;t to: &ldquo;Ph&aacute;!&rdquo; Đồng thời n&eacute;m thẳng về ph&iacute;a mặt b&eacute; trai. Nếu tr&uacute;ng phải đ&ograve;n n&agrave;y, n&oacute; ắt sẽ nổ tung đầu m&agrave; chết. Đ&uacute;ng v&agrave;o khoảnh khắc ng&agrave;n c&acirc;n treo sợi t&oacute;c, đột nhi&ecirc;n c&oacute; một vật x&eacute; gi&oacute; bay đến đ&aacute;nh rơi tấm b&ugrave;a, ghim n&oacute; xuống nền đất tuyết.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đạo sĩ ngưng thần nh&igrave;n kĩ, thấy tr&ecirc;n l&aacute; b&ugrave;a c&oacute; cắm một chiếc l&aacute; tr&uacute;c m&agrave;u xanh. Đ&oacute; r&otilde; r&agrave;ng chỉ l&agrave; một chiếc l&aacute; tr&uacute;c b&igrave;nh thường nhưng lại sắc b&eacute;n như dao, đ&acirc;m xuy&ecirc;n qua l&aacute; b&ugrave;a.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Sư phụ! Sư phụ!&rdquo; B&eacute; g&aacute;i mừng rỡ k&ecirc;u l&ecirc;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Sau tiếng reo mềm mại, ngọt ng&agrave;o ấy, một thanh ni&ecirc;n tuấn t&uacute; lững thững đi ra từ trong rừng. Người n&agrave;y mặt đẹp như ngọc, t&oacute;c d&agrave;i đen nh&aacute;nh, &aacute;o trắng hơn tuyết, trong tay cầm một c&acirc;y gậy tr&uacute;c m&agrave;u xanh. Tr&ocirc;ng y như đang ng&aacute;i ngủ, một tay đưa l&ecirc;n che miệng, ng&aacute;p d&agrave;i, sau đ&oacute; mới ngước mắt quan s&aacute;t t&igrave;nh h&igrave;nh trước mặt. Y chậm r&atilde;i bước đến, khẽ cốc l&ecirc;n đầu b&eacute; g&aacute;i một c&aacute;i, cất giọng hờ hững: &ldquo;Nha đầu Tiểu Tr&uacute;c kia, mi lại g&acirc;y họa cho ta rồi!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;C&aacute;i g&igrave; m&agrave; &ldquo;lại&rdquo; chứ, nếu kh&ocirc;ng v&igrave; muốn t&igrave;m đồ ăn cho sư phụ, Tiểu Tr&uacute;c chạy đến một nơi xa như thế n&agrave;y l&agrave;m g&igrave;?&rdquo; B&eacute; g&aacute;i một tay &ocirc;m đầu, một tay t&uacute;m lấy vạt &aacute;o của thanh ni&ecirc;n m&agrave; lay qua lay lại. &ldquo;Được rồi, được rồi, sư phụ, người muốn đ&aacute;nh, muốn mắng g&igrave; th&igrave; cứ để sau, b&acirc;y giờ c&oacute; người chẳng chịu n&oacute;i l&yacute; lẽ, lại c&ograve;n đ&aacute;nh cả trẻ con, người mau đứng ra ph&acirc;n xử gi&uacute;p con đi!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thanh ni&ecirc;n liếc nh&igrave;n đứa b&eacute; kia v&agrave; đạo sĩ, sau đ&oacute; chậm r&atilde;i đưa tay l&ecirc;n, b&uacute;ng ng&oacute;n tay nghe &ldquo;t&aacute;ch&rdquo; một c&aacute;i, lập tức c&oacute; một ngọn lửa b&ugrave;ng l&ecirc;n, th&igrave; ra l&aacute; b&ugrave;a tr&ecirc;n nền đất tuyết đ&atilde; bốc ch&aacute;y, sau nh&aacute;y mắt h&oacute;a th&agrave;nh tro t&agrave;n, bay đi tứ t&aacute;n. Điều kỳ lạ l&agrave; l&aacute; tr&uacute;c cắm tr&ecirc;n đ&oacute; kh&ocirc;ng hề bị tổn hại, vẫn xanh biếc như vừa được h&aacute;i từ tr&ecirc;n c&acirc;y xuống.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Chi&ecirc;u n&agrave;y thoạt nh&igrave;n th&igrave; b&igrave;nh thường nhưng lại đ&ograve;i hỏi ph&aacute;p lực cực kỳ cao si&ecirc;u, những kẻ đạo hạnh k&eacute;m cỏi đừng mong l&agrave;m được. Đạo sĩ &aacute;o t&iacute;m lập tức giơ tay &ocirc;m quyền l&agrave;m lễ. &ldquo;K&iacute;nh ch&agrave;o ti&ecirc;n qu&acirc;n, tại hạ l&agrave; Mộ Tử Ch&acirc;n, đại đệ tử của Thi&ecirc;n Huyền m&ocirc;n. H&ocirc;m nay v&igrave; Minh X&agrave; xuất hiện g&acirc;y hại cho nh&acirc;n gian, tệ m&ocirc;n mới đến đ&acirc;y trảm y&ecirc;u trừ ma, trả lại sự b&igrave;nh y&ecirc;n cho trăm họ.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;&Uacute;i ch&agrave;, ta n&oacute;i n&agrave;y tiểu huynh đệ.&rdquo; Thanh ni&ecirc;n &aacute;o trắng khẽ mỉm cười, đoạn d&ugrave;ng c&acirc;y gậy tr&uacute;c xanh chỉ về ph&iacute;a đứa b&eacute; m&igrave;nh người đu&ocirc;i rắn đang đứng chắn trước mặt Tiểu Tr&uacute;c, n&oacute;i: &ldquo;C&aacute;c ngươi cần sự y&ecirc;n b&igrave;nh, cố nhi&ecirc;n kh&ocirc;ng c&oacute; g&igrave; sai cả, nhưng lẽ n&agrave;o y&ecirc;u linh ở v&ugrave;ng n&uacute;i n&agrave;y kh&ocirc;ng cần sự y&ecirc;n b&igrave;nh hay sao?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mộ Tử Ch&acirc;n nh&iacute;u chặt đ&ocirc;i m&agrave;y, bu&ocirc;ng hai tay xuống, nghi&ecirc;m t&uacute;c n&oacute;i: &ldquo;Ti&ecirc;n qu&acirc;n n&oacute;i vậy sai rồi, t&agrave; ma ngoại đạo l&agrave; thứ m&agrave; ai cũng c&oacute; nghĩa vụ ti&ecirc;u diệt. Nếu kh&ocirc;ng phải y&ecirc;u nghiệt ho&agrave;nh h&agrave;nh, l&agrave;m hại đến người v&ocirc; tội, Thi&ecirc;n Huyền m&ocirc;n cớ chi lại v&ocirc; duy&ecirc;n v&ocirc; cớ đến đ&acirc;y trảm y&ecirc;u trừ ma?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thanh ni&ecirc;n &aacute;o trắng khẽ lắc đầu, chậm r&atilde;i n&oacute;i: &ldquo;Thế n&agrave;o l&agrave; &ldquo;y&ecirc;u nghiệt ho&agrave;nh h&agrave;nh&rdquo;? Lo&agrave;i người sống khắp Thần Ch&acirc;u, chẳng phải cũng đi tới đ&acirc;u ăn tới đ&oacute; đấy ư? Tất cả sống tr&ecirc;n đời đều mong kiếm miếng cơm ăn, đ&uacute;t cho đầy bụng, c&oacute; ai l&agrave; cao qu&yacute; hơn ai?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ch&iacute;nh phải, ch&iacute;nh phải, sư phụ n&oacute;i rất đ&uacute;ng!&rdquo; Tiểu Tr&uacute;c th&ograve; đầu ra từ sau lưng đứa b&eacute; trai, l&agrave;m mặt quỷ với Mộ Tử Ch&acirc;n, sau đ&oacute; trề m&ocirc;i n&oacute;i tiếp: &ldquo;Nếu cứ dựa theo c&aacute;i lẽ đ&oacute; m&agrave; t&iacute;nh, lẽ n&agrave;o đ&aacute;m chim bay tr&ecirc;n trời, th&uacute; chạy dưới đất cần phải hợp sức lại m&agrave; giết sạch lo&agrave;i người hay sao? N&oacute;i g&igrave; th&igrave; n&oacute;i, x&eacute;t cho c&ugrave;ng, lo&agrave;i người vẫn l&agrave; những kẻ ph&agrave;m ăn tục uống nhất, bất kể l&agrave; thứ ăn được hay kh&ocirc;ng ăn được cũng đều t&igrave;m c&aacute;ch bỏ bụng! Bất kể l&agrave; y&ecirc;u qu&aacute;i, động vật hay l&agrave; con người, l&agrave;m g&igrave; c&oacute; ai cao qu&yacute; hơn ai, dựa v&agrave;o c&aacute;i g&igrave; m&agrave; chỉ cho ph&eacute;p ngươi giết ta m&agrave; kh&ocirc;ng cho ph&eacute;p ta giết ngươi chứ?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mộ Tử Ch&acirc;n bất gi&aacute;c sững người, sau đ&oacute; nh&iacute;u chặt đ&ocirc;i m&agrave;y, giận dữ qu&aacute;t Tiểu Tr&uacute;c: &ldquo;L&agrave; một con người m&agrave; lại n&oacute;i ra những lời hoang đường qu&aacute; đỗi, ngươi rốt cuộc l&agrave; c&aacute;i thứ g&igrave; vậy?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiểu Tr&uacute;c cười h&igrave; h&igrave;, n&oacute;i: &ldquo;Cũng giống ngươi th&ocirc;i, ta l&agrave; con người, chẳng phải l&agrave; c&aacute;i thứ g&igrave; cả.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mộ Tử Ch&acirc;n cả giận, lập tức giơ cao trường kiếm, bắt kiếm quyết, trầm giọng n&oacute;i: &ldquo;Vị ti&ecirc;n qu&acirc;n n&agrave;y, ng&agrave;i đ&atilde; c&oacute; l&ograve;ng bao che cho y&ecirc;u nghiệt như thế, vậy th&igrave; chớ tr&aacute;ch ta động thủ. Cho d&ugrave; ng&agrave;i ph&aacute;p thuật phi ph&agrave;m, Mộ Tử Ch&acirc;n ta cũng chẳng phải hạng kh&ocirc;ng đ&aacute;nh m&agrave; lui. V&igrave; đạo nghĩa, ta sẵn s&agrave;ng liều cả t&iacute;nh mạng n&agrave;y.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thấy đối phương đ&atilde; b&agrave;y ra tư thế tấn c&ocirc;ng, thanh ni&ecirc;n &aacute;o trắng bất gi&aacute;c để lộ vẻ mặt sầu n&atilde;o, sau đ&oacute; khẽ thở d&agrave;i một tiếng, buồn bực n&oacute;i: &ldquo;C&aacute;i g&igrave; m&agrave; đạo nghĩa chứ, x&eacute;t cho c&ugrave;ng vẫn chỉ c&oacute; một c&acirc;u th&ocirc;i, phi ng&atilde; tộc loại, kỳ t&acirc;m tất dị<sup>1</sup>... &Ocirc;i, Thi&ecirc;n Huyền m&ocirc;n c&aacute;c ngươi thuộc d&ograve;ng Đạo gia, vốn ch&uacute; trọng c&aacute;i lẽ &ldquo;Nh&acirc;n ph&aacute;p Địa, Địa ph&aacute;p Thi&ecirc;n, Thi&ecirc;n ph&aacute;p Đạo, Đạo ph&aacute;p tự nhi&ecirc;n&rdquo;<sup>2</sup>, tại sao đến đời nh&agrave; ngươi th&igrave; lại đ&acirc;m ra u tối, cứng nhắc thế n&agrave;y? Thực đ&uacute;ng l&agrave; đời sau kh&ocirc;ng bằng đời trước!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">1</span></span></span></sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">. Một c&acirc;u n&oacute;i được ghi lại trong s&aacute;ch Tả truyện, &yacute; rằng: Nếu kh&ocirc;ng c&ugrave;ng d&ograve;ng giống với ta, l&ograve;ng dạ của họ ắt sẽ kh&aacute;c. (Mọi ch&uacute; th&iacute;ch trong s&aacute;ch đều l&agrave; của người dịch.)</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">2</span></span></span></sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">. Tr&iacute;ch Đạo Đức kinh, L&atilde;o Tử, dịch nghĩa: Người bắt chước Đất, Đất bắt chước Trời, Trời bắt chước Đạo, Đạo bắt chước tự nhi&ecirc;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;C&acirc;m miệng! Chớ c&oacute; l&agrave;m nhục sư m&ocirc;n ta!&rdquo; Mộ Tử Ch&acirc;n giận dữ xuất kiếm, lưỡi kiếm trong tay ng&acirc;n d&agrave;i kh&ocirc;ng dứt, &aacute;nh t&iacute;m bừng l&ecirc;n x&eacute; r&aacute;ch cả hư kh&ocirc;ng, tựa như một con rồng lớn lao thẳng về ph&iacute;a thanh ni&ecirc;n &aacute;o trắng. Thanh ni&ecirc;n &aacute;o trắng chẳng ch&uacute;t sợ h&atilde;i, thản nhi&ecirc;n l&ugrave;i lại một bước, dễ d&agrave;ng tr&aacute;nh khỏi sự tấn c&ocirc;ng của luồng kiếm kh&iacute; dữ dội. Mộ Tử Ch&acirc;n chẳng đợi chi&ecirc;u thức tung hết đ&atilde; bước nhanh về ph&iacute;a trước, thanh trường kiếm bừng l&ecirc;n những tia s&aacute;ng lạnh ngắt dưới &aacute;nh trăng, một người một kiếm m&agrave; tạo ra cả chục b&oacute;ng mờ, đồng loạt nhắm v&agrave;o thanh ni&ecirc;n &aacute;o trắng m&agrave; đ&acirc;m tới!</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thanh ni&ecirc;n &aacute;o trắng tựa như say rượu, lững thững l&ugrave;i về ph&iacute;a sau mấy bước. Bộ ph&aacute;p của y thoạt nh&igrave;n th&igrave; như rối loạn, nhưng bất kể Mộ Tử Ch&acirc;n c&oacute; biến chi&ecirc;u thế n&agrave;o, rốt cuộc vẫn chẳng thể chạm được v&agrave;o người y. Tay đạo sĩ dằn l&ograve;ng lại, lật cổ tay một c&aacute;i, hai ng&oacute;n tay tr&ecirc;n b&agrave;n tay tr&aacute;i kẹp chặt một tấm b&ugrave;a, rồi vận hết ph&aacute;p lực to&agrave;n th&acirc;n, sử ra chi&ecirc;u thức liều mạng V&ocirc; Ho&agrave;n Chi Đạo m&agrave; lao về ph&iacute;a thanh ni&ecirc;n &aacute;o trắng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">N&oacute;i th&igrave; chậm nhưng việc xảy ra rất nhanh, thanh ni&ecirc;n &aacute;o trắng tức tốc giơ c&acirc;y gậy tr&uacute;c xanh về ph&iacute;a trước. Chỉ thấy một c&aacute;i b&oacute;ng m&agrave;u xanh l&oacute;e l&ecirc;n, tốc độ nhanh như chớp giật, sau nh&aacute;y mắt đ&atilde; đ&aacute;nh tr&uacute;ng v&agrave;o ấn đường của Mộ Tử Ch&acirc;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Kiếm phong ngừng, kiếm kh&iacute; dứt. Đạo sĩ &aacute;o t&iacute;m nặng nề ng&atilde; gục xuống đất, bất tỉnh nh&acirc;n sự. Thanh ni&ecirc;n &aacute;o trắng chậm r&atilde;i thu c&acirc;y gậy tr&uacute;c xanh về, c&agrave;i v&agrave;o b&ecirc;n h&ocirc;ng, lắc đầu, bu&ocirc;ng lời cảm kh&aacute;i: &ldquo;Người trẻ tuổi n&agrave;y đ&uacute;ng l&agrave; thiển cận, c&oacute; c&aacute;i ch&iacute; kh&iacute; như thế th&igrave; đi l&agrave;m việc g&igrave; chẳng được, lại khơi khơi đ&ograve;i đồng quy vu tận với ta, m&agrave; ta c&oacute; th&ugrave; o&aacute;n g&igrave; s&acirc;u nặng với hắn đ&acirc;u cơ chứ...&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">N&oacute;i tới đ&acirc;y, thanh ni&ecirc;n &aacute;o trắng n&eacute;m từ trong tay &aacute;o ra một sợi d&acirc;y d&agrave;i m&agrave;u đen, quấn lấy eo của Mộ Tử Ch&acirc;n, sau đ&oacute; y phất nhẹ cổ tay phải, sợi d&acirc;y liền bay vọt l&ecirc;n, treo Mộ Tử Ch&acirc;n l&ecirc;n một c&agrave;nh t&ugrave;ng cao t&iacute;t. Tiểu Tr&uacute;c ngước khu&ocirc;n mặt nhỏ nhắn đỏ bừng l&ecirc;n nh&igrave;n người bị treo tr&ecirc;n c&acirc;y, vỗ tay cười n&oacute;i: &ldquo;Ha ha, t&ecirc;n hung dữ biến th&agrave;nh một con s&acirc;u mềm nhũn rồi! C&oacute; điều b&acirc;y giờ trời đang lạnh thế, hắn c&oacute; bị đ&ocirc;ng cứng lại kh&ocirc;ng nhỉ?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Kh&ocirc;ng sao đ&acirc;u, t&ecirc;n tiểu tử n&agrave;y tu vi kh&ocirc;ng tệ, c&ugrave;ng lắm l&agrave; nửa canh giờ nữa sẽ tỉnh lại, ắt chẳng c&oacute; vấn đề g&igrave; đ&aacute;ng ngại.&rdq');
INSERT INTO `sach` (`id`, `tensach`, `tacgia`, `anh`, `noidung`) VALUES
(3, 'Anh Hùng Ca 1 Chương 2', 'Lê Tuấn', 'anhungca1.jpg', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Chương 2</span></span></span></em><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">KHỞI H&Agrave;NH</span></span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mười năm sau. Đang dịp cuối xu&acirc;n đầu hạ, hoa nở như gấm khắp n&uacute;i rừng, c&acirc;y cối xanh tươi mơn mởn. Tr&ecirc;n đỉnh một ngọn n&uacute;i biếc xanh c&oacute; một ng&ocirc;i nh&agrave; tr&uacute;c nhỏ, b&ecirc;n ngo&agrave;i kh&ocirc;ng hề dựng h&agrave;ng r&agrave;o, chỉ c&oacute; một khu rừng tr&uacute;c trải d&agrave;i bất tận, gần như phủ k&iacute;n một nửa ngọn n&uacute;i. B&ecirc;n ngo&agrave;i rừng tr&uacute;c c&oacute; mấy khu vườn rau trồng cải xanh, cải trắng, c&ograve;n c&oacute; cả những quả c&agrave; t&iacute;m tr&ograve;n xoe căng đầy, tuy kh&ocirc;ng được rạng rỡ như hoa nhưng tất thảy đều tươi mới, mang một n&eacute;t đẹp ri&ecirc;ng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ợ!&rdquo; Từ trong ng&ocirc;i nh&agrave; tr&uacute;c vang ra một tiếng ợ rất to. Chỉ thấy một thanh ni&ecirc;n tuấn t&uacute; t&oacute;c đen &aacute;o trắng đứng dậy b&ecirc;n cạnh chiếc b&agrave;n để đầy b&aacute;t đĩa trống kh&ocirc;ng, sau đ&oacute; một tay vỗ bụng, một tay lấy tăm xỉa răng. Sau khi xỉa răng xong, y h&agrave;i l&ograve;ng, gật đầu n&oacute;i: &ldquo;M&oacute;n c&aacute; xốt c&agrave; chua n&agrave;y nấu kh&ocirc;ng tệ, nha đầu mau thu dọn đi, l&aacute;t nữa ta sẽ dạy mi một m&ocirc;n ph&aacute;p thuật mới.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Vị c&ocirc; nương được gọi l&agrave; &ldquo;nha đầu&rdquo; kia xem chừng c&ograve;n chưa đầy hai mươi tuổi, mặt m&agrave;y thanh t&uacute;, răng trắng mắt s&aacute;ng, mặc một bộ v&aacute;y &aacute;o m&agrave;u xanh lục, b&ecirc;n h&ocirc;ng đeo một chiếc hồ l&ocirc; ngọc phỉ th&uacute;y nhỏ nhắn. Nghe thấy thanh ni&ecirc;n gọi, n&agrave;ng liền đi tới thu dọn b&aacute;t đĩa mang v&agrave;o trong bếp, đồng thời ngoảnh đầu lại l&agrave;m mặt quỷ với sư phụ của m&igrave;nh. &ldquo;Học ph&aacute;p thuật th&igrave; c&oacute; &iacute;ch g&igrave; chứ? N&oacute; kh&ocirc;ng thể gi&uacute;p con h&aacute;i rau, cũng kh&ocirc;ng thể gi&uacute;p con rửa b&aacute;t, cho d&ugrave; con c&oacute; học thế n&agrave;o đi nữa th&igrave; cũng kh&ocirc;ng thể đ&aacute;nh lại sư phụ, c&agrave;ng kh&ocirc;ng thể bắt sư phụ l&agrave;m cơm cho con ăn được.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;N&agrave;y n&agrave;y, l&uacute;c ta nhặt được mi trong rừng tr&uacute;c, mi c&ograve;n chưa cao bằng c&aacute;i ch&acirc;n ghế n&agrave;y. Khi đ&oacute;, nếu kh&ocirc;ng phải ta l&agrave;m cơm cho mi ăn, lẽ n&agrave;o l&agrave; mi l&agrave;m cơm cho ta ăn chắc?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nghe thấy những lời phản b&aacute;c của sư phụ, c&ocirc; nương đ&oacute; liền chụp lấy một miếng b&aacute;nh hoa quế, vung tay n&eacute;m ra ngo&agrave;i. &ldquo;Phải rồi, c&oacute; điều người chỉ to&agrave;n nấu canh l&aacute; tr&uacute;c m&agrave; th&ocirc;i, kh&aacute; hơn một ch&uacute;t cũng chỉ l&agrave; măng luộc. Con đ&acirc;y c&oacute; thể sống được đến b&acirc;y giờ thật chẳng phải l&agrave; điều dễ d&agrave;ng g&igrave;.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Miếng b&aacute;nh hoa quế đẹp mắt như được buộc d&acirc;y, sau khi bay một v&ograve;ng giữa kh&ocirc;ng trung liền lao về hướng người thanh ni&ecirc;n. Người thanh ni&ecirc;n h&aacute; miệng ra đ&oacute;n lấy miếng b&aacute;nh, sau khi nhai ch&oacute;p ch&eacute;p một l&uacute;c liền b&uacute;ng ng&oacute;n tay nghe &ldquo;t&aacute;ch&rdquo; một tiếng, tức th&igrave; trong ph&ograve;ng xuất hiện mấy d&ograve;ng nước trong veo. Mấy d&ograve;ng nước đ&oacute; chẳng kh&aacute;c n&agrave;o những con rồng nhỏ m&agrave;u trắng bạc, sau khi bay qua ph&iacute;a b&ecirc;n tr&ecirc;n những chiếc b&aacute;t đĩa d&iacute;nh đầy dầu mỡ, tất cả b&aacute;t đĩa lập tức được rửa sạch bong như mới.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Thế n&agrave;o?&rdquo; Người thanh ni&ecirc;n đắc &yacute; hỏi. &ldquo;Chi&ecirc;u n&agrave;y mi c&oacute; muốn học kh&ocirc;ng?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">C&ocirc; nương kia liền lao vọt tới, &ocirc;m lấy c&aacute;nh tay của y m&agrave; lay kh&ocirc;ng ngừng. &ldquo;Sư phụ xấu qu&aacute; đi, c&oacute; chi&ecirc;u n&agrave;y m&agrave; sao kh&ocirc;ng sớm dạy cho con? Hại con phải rửa b&aacute;t đĩa mười mấy năm trời!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Trời đ&atilde; trao nhiệm vụ cao cả cho ai, trước ti&ecirc;n sẽ khiến người đ&oacute; phải mệt g&acirc;n mỏi cốt...&rdquo; Thanh ni&ecirc;n vừa n&oacute;i vừa gật g&agrave; gật g&ugrave;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Bữa tối h&ocirc;m nay, sự phụ đi m&agrave; gặm tr&uacute;c nh&eacute;!&rdquo; Vị c&ocirc; nương khoanh tr&ograve;n hai tay trước ngực, tuy&ecirc;n bố b&atilde;i c&ocirc;ng. &ldquo;Ta sai rồi, ta sai rồi m&agrave;, tại ta đ&atilde;ng tr&iacute; n&ecirc;n mới qu&ecirc;n mất đấy th&ocirc;i...&rdquo; Thanh ni&ecirc;n lập tức hạ giọng cầu xin.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Sao sư phụ kh&ocirc;ng qu&ecirc;n lu&ocirc;n chuyện ăn cơm đi?&rdquo; Một &aacute;nh mắt sắc b&eacute;n lập tức chiếu tới.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Được rồi, được rồi m&agrave;, mất b&ograve; mới lo l&agrave;m chuồng cũng chưa t&iacute;nh l&agrave; qu&aacute; muộn, b&acirc;y giờ sư phụ sẽ dạy cho con lu&ocirc;n đ&acirc;y.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Dứt lời, thanh ni&ecirc;n bước ra khỏi căn nh&agrave; tr&uacute;c, dừng ch&acirc;n lại giữa c&aacute;i s&acirc;n nhỏ ở ngay ngo&agrave;i cửa. &Aacute;nh dương d&igrave;u dịu đầu chiều trải xuống khắp s&acirc;n, bao phủ l&ecirc;n cả những ngọn n&uacute;i xanh biếc xung quanh. L&agrave;n gi&oacute; cuối xu&acirc;n ấm &aacute;p thổi qua những c&agrave;nh tr&uacute;c, ph&aacute;t ra tiếng x&agrave;o xạc nhẹ nh&agrave;ng. M&iacute; mắt người thanh ni&ecirc;n dần trở n&ecirc;n nặng trĩu, y nheo mắt lại, bước l&ecirc;n ph&iacute;a trước một bước, hai tay &ocirc;m th&agrave;nh một v&ograve;ng tr&ograve;n giữa kh&ocirc;ng trung, tay phải ở tr&ecirc;n, tay tr&aacute;i ở dưới, hai l&ograve;ng b&agrave;n tay hướng v&agrave;o nhau. &ldquo;Nha đầu, ch&uacute; &yacute; cảm nhận sự biến h&oacute;a trong cơ thể đấy...&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">C&ocirc; nương xinh đẹp lập tức l&agrave;m theo. Động t&aacute;c n&agrave;y l&agrave; tư thế khởi đầu trong khi tu luyện, n&agrave;ng theo sư phụ nhiều năm, sớm đ&atilde; thuần thục lắm rồi, chẳng bao l&acirc;u sau đ&atilde; cảm nhận được sự chuyển động của những d&ograve;ng kh&iacute; n&oacute;ng trong cơ thể. Kế đ&oacute;, n&agrave;ng lẳng lặng chờ đợi sư phụ m&igrave;nh đưa ra chỉ thị tiếp theo, vậy nhưng chờ suốt một hồi l&acirc;u m&agrave; đối phương vẫn chẳng c&oacute; động tĩnh g&igrave;. Nguyệt Tiểu Tr&uacute;c hơi nhướng m&agrave;y l&ecirc;n, thu tư thế lại, sau đ&oacute; rảo bước đi tới trước mặt thanh ni&ecirc;n, thấy hai mắt đối phương đ&atilde; h&iacute;p lại th&agrave;nh một đường thẳng, hiển nhi&ecirc;n sắp ch&igrave;m v&agrave;o giấc ngủ.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Sư phụ!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiếng gọi của vị c&ocirc; nương khiến người thanh ni&ecirc;n r&ugrave;ng m&igrave;nh một c&aacute;i, kh&oacute;i trắng lập tức bốc l&ecirc;n, thanh ni&ecirc;n tuấn t&uacute; sau nh&aacute;y mắt đ&atilde; biến th&agrave;nh một con gấu tr&uacute;c tr&ograve;n xoe b&eacute;o n&uacute;c. Sau khi hiện nguy&ecirc;n h&igrave;nh, gấu tr&uacute;c liền b&ograve; rạp xuống đất, th&iacute;ch th&uacute; hưởng thụ &aacute;nh dương ấm &aacute;p chiếu l&ecirc;n người. Thấy bộ dạng n&agrave;y của sư phụ, Tiểu Tr&uacute;c dở kh&oacute;c dở cười, miệng lẩm bẩm: &ldquo;M&igrave;nh biết ngay m&agrave;&rdquo;, sau đ&oacute; liền ngồi xuống b&ecirc;n cạnh gấu tr&uacute;c, tựa người v&agrave;o lớp l&ocirc;ng nửa đen nửa trắng mềm mại, cảm nhận bầu kh&ocirc;ng kh&iacute; thanh b&igrave;nh xung quanh.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Kh&ocirc;ng biết qua bao l&acirc;u sau, đang trong cơn mơ m&agrave;ng, Tiểu Tr&uacute;c chợt cảm thấy mặt đất hơi chấn động, dường như c&oacute; một tr&agrave;ng tiếng v&oacute; ngựa tới gần dưới ch&acirc;n n&uacute;i. N&agrave;ng lập tức mở mắt, thấy sư phụ của m&igrave;nh đ&atilde; biến th&agrave;nh h&igrave;nh người tự l&uacute;c n&agrave;o. L&uacute;c n&agrave;y y đang ưỡn thẳng lưng, đứng lặng lẽ ngay trước cổng, vẻ mặt nặng nề chưa từng c&oacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiểu Tr&uacute;c vừa định l&ecirc;n tiếng d&ograve; hỏi th&igrave; chợt thấy giữa nơi rừng n&uacute;i, chim ch&oacute;c bay loạn xạ, từ bốn hướng đ&ocirc;ng, t&acirc;y, nam, bắc c&oacute; bốn thanh trường kiếm đồng loạt bay vụt l&ecirc;n, treo lửng lơ giữa đất trời. Bốn thanh kiếm c&ugrave;ng ng&acirc;n vang, sau đ&oacute; chiếu ra bốn luồng &aacute;nh s&aacute;ng ch&oacute;i mắt. Thanh kiếm ở ph&iacute;a bắc chiếu ra &aacute;nh s&aacute;ng m&agrave;u trắng bạc, thanh kiếm ở ph&iacute;a nam chiếu ra &aacute;nh s&aacute;ng m&agrave;u đỏ thẫm, thanh kiếm ở ph&iacute;a đ&ocirc;ng chiếu ra &aacute;nh s&aacute;ng m&agrave;u xanh lục, thanh kiếm ở ph&iacute;a t&acirc;y chiếu ra &aacute;nh s&aacute;ng m&agrave;u v&agrave;ng kim. Bốn luồng &aacute;nh s&aacute;ng n&agrave;y giao thoa với nhau, tạo n&ecirc;n một h&igrave;nh vu&ocirc;ng, ho&agrave;n to&agrave;n phong tỏa ngọn n&uacute;i.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Tứ Tượng trận?&rdquo; Tiểu Tr&uacute;c kinh h&atilde;i k&ecirc;u l&ecirc;n. N&agrave;ng tuy l&agrave; con người, tư chất tu h&agrave;nh cũng chỉ thuộc h&agrave;ng trung đẳng, nhưng nếu x&eacute;t về khả năng ghi nhớ th&igrave; lại chẳng mấy ai so được. Mười mấy năm nay, cứ mỗi khi rảnh rỗi l&agrave; n&agrave;ng lại mang đống kỳ thư của sư phụ ra đọc, kiến thức c&oacute; thể n&oacute;i l&agrave; cực kỳ uy&ecirc;n th&acirc;m. Trận ph&aacute;p do bốn thanh kiếm kia tạo th&agrave;nh ch&iacute;nh l&agrave; Tứ Tượng trận nổi danh thi&ecirc;n hạ của Thi&ecirc;n Huyền m&ocirc;n, theo truyền thuyết c&oacute; thể phong ấn được tất thảy mọi thứ tr&ecirc;n đời, đừng n&oacute;i l&agrave; m&atilde;nh th&uacute; hay y&ecirc;u linh, cho d&ugrave; l&agrave; địa ti&ecirc;n cũng chớ h&ograve;ng tho&aacute;t ra khỏi n&oacute;. Quả nhi&ecirc;n, những con chim đang nh&aacute;o nh&aacute;c bỏ chạy kia như bị va phải một tấm lưới lớn v&ocirc; h&igrave;nh giữa trời đất, lần lượt rơi xuống.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nguyệt Tiểu Tr&uacute;c lo lắng đưa mắt nh&igrave;n sư phụ, nhưng y chẳng buồn quay đầu lại, chỉ cất giọng hờ hững: &ldquo;Nha đầu, v&agrave;o trong nh&agrave; trước đi!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tuy thường ng&agrave;y hay c&atilde;i cọ với sư phụ, nhưng mỗi lần gặp chuyện nghi&ecirc;m trọng, Nguyệt Tiểu Tr&uacute;c đều kh&ocirc;ng bao giờ l&agrave;m tr&aacute;i &yacute; người đ&atilde; nu&ocirc;i dưỡng m&igrave;nh lớn kh&ocirc;n. N&agrave;ng khẽ gật đầu một c&aacute;i, &acirc;n cần dặn d&ograve;: &ldquo;Sư phụ, người phải cẩn thận đấy!&rdquo; Sau đ&oacute; liền quay v&agrave;o trong nh&agrave;, l&eacute;n quan s&aacute;t t&igrave;nh h&igrave;nh b&ecirc;n ngo&agrave;i qua khe cửa sổ.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Kh&ocirc;ng l&acirc;u sau, chỉ thấy c&oacute; s&aacute;u nam nh&acirc;n cao lớn chạy ra từ tr&ecirc;n con đường n&uacute;i ngợp b&oacute;ng c&acirc;y xanh. Bọn họ ai nấy đều đội mũ cao, mặc đạo b&agrave;o m&agrave;u xanh da trời, b&ecirc;n h&ocirc;ng đeo trường kiếm, trang phục cực kỳ giống với &ldquo;t&ecirc;n hung dữ&rdquo; Mộ Tử Ch&acirc;n của mười năm về trước. Sau chốc l&aacute;t, lại c&oacute; hơn hai mươi người nữa xuất hiện, song trang phục rất kh&aacute;c so với s&aacute;u người kia.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Một nh&oacute;m tay cầm b&uacute;t sắt chạm trổ h&igrave;nh m&acirc;y, m&igrave;nh kho&aacute;c trường b&agrave;o đỏ rực, tr&ecirc;n tay &aacute;o v&agrave; cổ &aacute;o đều th&ecirc;u h&igrave;nh m&acirc;y l&agrave;nh m&agrave;u trắng bạc; một nh&oacute;m tay cầm trường k&iacute;ch, ăn mặc theo lối người luyện v&otilde;, tr&ecirc;n chiếc &aacute;o m&agrave;u đen c&oacute; th&ecirc;u h&igrave;nh một con rồng v&agrave;ng nhe nanh m&uacute;a vuốt trải d&agrave;i từ giữa ngực xuống tới h&ocirc;ng; một nh&oacute;m tay cầm phất trần m&agrave;u x&aacute;m, t&oacute;c b&uacute;i cao tr&ecirc;n đầu, m&igrave;nh mặc &aacute;o d&agrave;i m&agrave;u xanh với ống tay &aacute;o rộng, bộ dạng hệt như một thư sinh b&igrave;nh thường, b&ecirc;n h&ocirc;ng th&igrave; lại đeo m&otilde; v&agrave; tr&agrave;ng hạt.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Ch&iacute;nh v&agrave;o l&uacute;c n&agrave;y, một nam tử đeo kiếm chậm r&atilde;i bước ra từ giữa đ&aacute;m đ&ocirc;ng. Người n&agrave;y kh&ocirc;ng phải ai xa lạ, ch&iacute;nh l&agrave; Mộ Tử Ch&acirc;n đ&atilde; từng c&oacute; duy&ecirc;n gặp mặt Tiểu Tr&uacute;c một lần. Chỉ thấy y d&otilde;i mắt nh&igrave;n về ph&iacute;a thanh ni&ecirc;n, trầm giọng n&oacute;i: &ldquo;Mặc Bạch ti&ecirc;n qu&acirc;n, l&acirc;u rồi kh&ocirc;ng gặp, xem chừng ng&agrave;i vẫn mạnh giỏi như thuở n&agrave;o.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mặc Bạch tho&aacute;ng nhếch m&ocirc;i cười khẽ, nhưng nơi đ&aacute;y mắt lại chẳng hề c&oacute; n&eacute;t cười. &ldquo;Lần n&agrave;y c&aacute;c ngươi l&ecirc;n n&uacute;i, c&oacute; mạnh giỏi e cũng sẽ biến th&agrave;nh kh&ocirc;ng mạnh giỏi. Rốt cuộc l&agrave; cơn gi&oacute; độc n&agrave;o đ&atilde; thổi cả tứ đại ph&aacute;i tru y&ecirc;u c&aacute;c ngươi tới v&ugrave;ng đất khỉ ho c&ograve; g&aacute;y n&agrave;y của ta vậy?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Th&igrave; ra nh&oacute;m hai mươi t&aacute;m người n&agrave;y đến từ tứ đại m&ocirc;n ph&aacute;i đang chấp h&agrave;nh Tru Y&ecirc;u lệnh.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thi&ecirc;n Huyền m&ocirc;n kiếm ph&aacute;p v&ocirc; song, kiếm trận lại c&agrave;ng tinh diệu. Theo truyền thuyết, cao nh&acirc;n trong Thi&ecirc;n Huyền m&ocirc;n c&oacute; thể ngự kiếm phi h&agrave;nh, ng&agrave;y bay ng&agrave;n dặm.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">X&iacute;ch V&acirc;n l&acirc;u nổi tiếng với Đan Chu thiết b&uacute;t, ph&aacute;p thuật phi ph&agrave;m. B&ugrave;a hộ th&acirc;n m&agrave; ph&aacute;i n&agrave;y vẽ ra c&ograve;n c&oacute; c&ocirc;ng hiệu cực kỳ lợi hại, l&agrave; thứ m&agrave; trăm họ ai ai cũng muốn c&oacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Độ Tội cốc sở trường về v&otilde; thuật, to&agrave;n thể đệ tử trong cốc đều tin rằng chỉ c&oacute; d&ugrave;ng v&otilde; để trị thi&ecirc;n hạ mới c&oacute; thể qu&eacute;t sạch mọi tội &aacute;c tr&ecirc;n thế gian, trả lại cho Thần Ch&acirc;u sự y&ecirc;n b&igrave;nh.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thập Phương điện chuy&ecirc;n về luyện đan v&agrave; chế thuốc, c&oacute; biệt hiệu l&agrave; &ldquo;Thập Phương th&aacute;m tầm Nho Th&iacute;ch Đạo, Thập điện Di&ecirc;m la bất cảm thu&rdquo;<sup>1</sup>. M&ocirc;n ph&aacute;i n&agrave;y cho rằng cần phải hợp nhất tam gi&aacute;o Nho, Th&iacute;ch, Đạo th&igrave; mới c&oacute; thể cứu được l&ecirc; d&acirc;n trăm họ khỏi cơn nước lửa.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">1</span></span></span></sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">. Thập Phương điện dốc l&ograve;ng t&igrave;m hiểu về tam gi&aacute;o Nho, Th&iacute;ch, Đạo, người bệnh m&agrave; Thập Phương điện chạy chữa, Thập điện Di&ecirc;m Vương cũng chẳng d&aacute;m lấy đi t&iacute;nh mạng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Hiện giờ, tứ đại m&ocirc;n ph&aacute;i tề tụ tr&ecirc;n n&uacute;i Thanh Xuy&ecirc;n, c&ograve;n d&ugrave;ng Tứ Tượng trận để tiến h&agrave;nh phong tỏa, nh&igrave;n bộ dạng th&igrave; hiển nhi&ecirc;n l&agrave; chẳng hề c&oacute; &yacute; tốt. Một g&atilde; v&otilde; sĩ của Độ Tội cốc bước l&ecirc;n ph&iacute;a trước một bước, nện mạnh thanh trường k&iacute;ch trong tay xuống đất l&agrave;m bụi đất bốc l&ecirc;n m&ugrave; mịt, sau đ&oacute; gằn giọng n&oacute;i: &ldquo;Gọi ngươi một tiếng ti&ecirc;n qu&acirc;n chẳng qua l&agrave; nể mặt ngươi th&ocirc;i, n&oacute;i trắng ra ngươi chỉ l&agrave; một con d&atilde; th&uacute; nơi rừng r&uacute;, nhờ cơ duy&ecirc;n xảo hợp n&ecirc;n mới tu luyện được th&agrave;nh địa ti&ecirc;n. Mặc Bạch, chỉ cần ngươi chịu giao ra V&acirc;n Sinh k&iacute;nh, bọn ta tuyệt đối sẽ kh&ocirc;ng l&agrave;m kh&oacute; ngươi, nhược bằng ngươi rượu mời kh&ocirc;ng uống, muốn uống rượu phạt, vậy th&igrave; chớ tr&aacute;ch bọn ta...&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">G&atilde; v&otilde; sĩ c&ograve;n chưa dứt lời, Mặc Bạch đột nhi&ecirc;n b&uacute;ng ng&oacute;n tay một c&aacute;i rất vang. Lập tức c&oacute; một mũi d&ugrave;i băng lấp l&aacute;nh &aacute;nh s&aacute;ng xuất hiện dưới &aacute;nh mặt trời rực rỡ, lao thẳng về ph&iacute;a mặt của g&atilde; v&otilde; sĩ. Hắn ta biến hẳn sắc mặt, lập tức vung thanh trường k&iacute;ch m&agrave;u bạc của m&igrave;nh l&ecirc;n, muốn ch&eacute;m rơi mũi d&ugrave;i băng kia. C&oacute; điều, động t&aacute;c của hắn tuy nhanh nhưng tốc độ của mũi d&ugrave;i băng c&ograve;n nhanh hơn, chừng như chuẩn bị đ&acirc;m thẳng v&agrave;o ấn đường của hắn. Đột nhi&ecirc;n, mũi d&ugrave;i băng nổ tung khi c&ograve;n c&aacute;ch tr&aacute;n g&atilde; v&otilde; sĩ chưa đầy nửa tấc, những mảnh băng vụn tựa như v&ocirc; số ng&ocirc;i sao lấp l&aacute;nh tỏa ra xung quanh, bịt chặt miệng g&atilde; v&otilde; sĩ lại, khiến hắn chẳng thể n&oacute;i nổi một c&acirc;u n&agrave;o, chỉ c&oacute; thể ph&aacute;t ra những &acirc;m thanh &ldquo;&uacute; ớ&rdquo;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thấy Mặc Bạch ra tay, s&aacute;u t&ecirc;n v&otilde; sĩ c&ograve;n lại của Độ Tội cốc đều giơ cao vũ kh&iacute;, v&acirc;y chặt y v&agrave;o giữa. Trong số đ&oacute; c&oacute; một nữ tử mặc đồ b&oacute; s&aacute;t người, tay cầm b&aacute;n nguyệt k&iacute;ch, qu&aacute;t lớn: &ldquo;Ngươi th&acirc;n l&agrave; địa ti&ecirc;n, vậy m&agrave; lại ra tay với lo&agrave;i người, ỷ lớn hiếp nhỏ, thực l&agrave; bỉ ổi!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;X&eacute;t về sự bỉ ổi, ta c&ograve;n l&acirc;u mới so được với đ&aacute;m người đầy bụng thi thư c&aacute;c ngươi. Rặt một lũ chỉ muốn lấy đ&ocirc;ng hiếp &iacute;t, ỷ mạnh hiếp yếu.&rdquo; Mặc Bạch cười khẩy một tiếng, cất lời giễu cợt. &ldquo;Hơn nữa, vừa rồi hắn n&oacute;i kh&ocirc;ng sai, ta chỉ l&agrave; một con d&atilde; th&uacute; nơi rừng r&uacute;, đ&acirc;u hiểu g&igrave; về t&ocirc;n ti lớn nhỏ, chỉ biết rằng kẻ n&agrave;o d&aacute;m x&uacute;c phạm ta, d&ugrave; ở xa ta cũng phải diệt trừ!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mặc Bạch thường ng&agrave;y vẫn lu&ocirc;n uể oải lười nh&aacute;c, bộ dạng l&uacute;c n&agrave;o cũng như thiếu ngủ, ấy vậy m&agrave; l&uacute;c n&agrave;y cặp mắt lại s&aacute;ng như sao, hờ hững liếc nh&igrave;n mọi người một lượt.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Thấy y tức giận, Mộ Tử Ch&acirc;n bước tới chặn đ&aacute;m người của Độ Tội cốc lại, sau đ&oacute; quay qua nh&igrave;n Mặc Bạch, trầm giọng n&oacute;i: &ldquo;Ti&ecirc;n qu&acirc;n, Kinh huynh t&iacute;nh t&igrave;nh thẳng thắn, vừa rồi quả thực c&oacute; điều đắc tội. C&oacute; điều, v&igrave; sự việc đang hồi khẩn cấp n&ecirc;n y mới n&oacute;i chẳng lựa lời.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Kh&ocirc;ng giấu g&igrave; ti&ecirc;n qu&acirc;n, lần n&agrave;y ch&uacute;ng t&ocirc;i tới đ&acirc;y l&agrave; muốn mượn ti&ecirc;n qu&acirc;n một m&oacute;n bảo vật...&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Mượn?&rdquo; Mặc Bạch cười lạnh một tiếng, cắt ngang lời đối phương. &ldquo;Đ&atilde; c&oacute; việc muốn nhờ người kh&aacute;c, vậy th&igrave; cần phải c&oacute; th&aacute;i độ đ&uacute;ng mực mới được. C&aacute;c ngươi phong tỏa ngọn n&uacute;i n&agrave;y, c&ograve;n x&ocirc;ng v&agrave;o nơi ở của ta m&agrave; h&ocirc; to gọi nhỏ, tr&ecirc;n đời n&agrave;y c&oacute; kiểu &ldquo;mượn&rdquo; n&agrave;o như của c&aacute;c ngươi kh&ocirc;ng?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nghe thấy những lời n&agrave;y của y, đ&aacute;m người của Độ Tội cốc ngơ ng&aacute;c nh&igrave;n nhau, t&ecirc;n v&otilde; sĩ khi n&atilde;y đứng ra lau những mảnh băng vụn b&ecirc;n kh&oacute;e miệng, nhưng vừa định n&oacute;i g&igrave; đ&oacute; th&igrave; đ&atilde; bị sư tỷ của m&igrave;nh d&ugrave;ng &aacute;nh mắt chặn lại. Người dẫn đầu của X&iacute;ch V&acirc;n l&acirc;u l&agrave; một thanh ni&ecirc;n mặc &aacute;o đỏ, tay cầm Đan Chu thiết b&uacute;t, y chậm r&atilde;i đi tới trước mặt Mặc Bạch, khom người v&aacute;i một c&aacute;i thật s&acirc;u, n&oacute;i: &ldquo;V&atilde;n bối Tất Phi xin được b&aacute;i kiến tiền bối. Thực kh&ocirc;ng d&aacute;m giấu, v&atilde;n bối quả thực c&oacute; chuyện muốn cậy nhờ.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">G&atilde; Tất Phi n&agrave;y mi thanh mục t&uacute;, v&oacute;c người mảnh khảnh, to&agrave;n th&acirc;n to&aacute;t ra kh&iacute; chất tho&aacute;t tục, chẳng vướng bụi trần. Đ&aacute;ng tiếc, tuy y phong th&aacute;i ngời ngời nhưng ch&acirc;n tr&aacute;i lại hơi c&oacute; tật, vừa rồi chỉ mới bước đi v&agrave;i bước đ&atilde; để lộ vẻ khập khiễng. Sau khi khom người h&agrave;nh lễ, y đứng thẳng người dậy, cất giọng sang sảng n&oacute;i: &ldquo;Mười năm trước, hai thượng cổ thần ma l&agrave; Ứng Long v&agrave; Tương Liễu đại chiến với nhau nơi Đ&ocirc;ng Hải, l&agrave;m cho trời long đất lở, sinh linh đồ th&aacute;n, người chết nhiều kh&ocirc;ng kể xiết. Thảm trạng năm xưa chắc hẳn tiền bối cũng c&oacute; nghe n&oacute;i rồi.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Kh&aacute;c với vẻ hằm h&egrave; khi&ecirc;u kh&iacute;ch của những người kh&aacute;c, Tất Phi khi mở lời đ&atilde; cất c&acirc;y b&uacute;t sắt của m&igrave;nh v&agrave;o trong tay &aacute;o. Thấy đối phương lời lẽ khi&ecirc;m cung, lễ số đầy đủ, sắc mặt Mặc Bạch d&atilde;n ra đ&ocirc;i phần, rồi y chắp tay sau lưng, hờ hững n&oacute;i: &ldquo;Đ&uacute;ng vậy.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Vậy chắc tiền bối cũng biết, năm xưa chưởng m&ocirc;n của tứ đại m&ocirc;n ph&aacute;i l&agrave; Thi&ecirc;n Huyền m&ocirc;n, X&iacute;ch V&acirc;n l&acirc;u, Độ Tội cốc, Thập Phương điện đ&atilde; c&ugrave;ng nhau lập ra Tru Y&ecirc;u lệnh, bắt đầu hạ s&aacute;t tất thảy y&ecirc;u linh dị th&uacute; chốn nh&acirc;n gian. Khi đ&oacute;, Thần Ch&acirc;u l&acirc;m nạn, kh&ocirc;ng chỉ trăm họ b&igrave;nh thường lang bạt kỳ hồ, ngay cả đ&aacute;m y&ecirc;u linh cũng chẳng c&oacute; chỗ n&aacute;u th&acirc;n...&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">N&oacute;i tới đ&acirc;y, Tất Phi thở d&agrave;i một tiếng, tr&ecirc;n mặt lộ vẻ x&oacute;t thương, rồi lại n&oacute;i tiếp với Mặc Bạch: &ldquo;Nhưng c&oacute; điều n&agrave;y chắc tiền bối c&ograve;n chưa biết, tứ đại m&ocirc;n ph&aacute;i hợp lực tru y&ecirc;u thực ra c&ograve;n c&oacute; nguy&ecirc;n nh&acirc;n kh&aacute;c. Năm đ&oacute;, cuộc chiến giữa Ứng Long v&agrave; Tương Liễu k&eacute;o d&agrave;i tới cả trăm ng&agrave;y, kh&ocirc;ng chỉ chưa ph&acirc;n thắng bại m&agrave; c&ograve;n c&oacute; xu thế c&agrave;ng l&uacute;c c&agrave;ng m&atilde;nh liệt, bất cứ l&uacute;c n&agrave;o cũng c&oacute; thể x&ocirc;ng v&agrave;o nội địa Thần Ch&acirc;u rồi đ&aacute;nh tiếp. V&igrave; điều n&agrave;y, ch&uacute;ng sinh trong thi&ecirc;n hạ phải chịu đủ nỗi khổ sở, nhưng thần linh tr&ecirc;n trời vẫn chẳng bận t&acirc;m, Phật Tổ chốn T&acirc;y Thi&ecirc;n cũng kh&ocirc;ng buồn ng&oacute; đến, thế n&ecirc;n lo&agrave;i người đ&agrave;nh t&igrave;m c&aacute;ch tự cứu m&igrave;nh. Khi đ&oacute;, chưởng m&ocirc;n của tứ đại m&ocirc;n ph&aacute;i đ&atilde; b&agrave;n bạc với nhau, cuối c&ugrave;ng nghĩ ra được biện ph&aacute;p duy nhất để phong ấn Ứng Long v&agrave; Tương Liễu, đ&oacute; l&agrave; tập hợp nội đan của y&ecirc;u linh trong thi&ecirc;n hạ, d&ugrave;ng y&ecirc;u lực l&agrave;m v&aacute;ch ngăn, như vậy mới c&oacute; thể giam cầm Ứng Long v&agrave; Tương Liễu ở b&ecirc;n bờ Đ&ocirc;ng Hải.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nghe Tất Phi n&oacute;i vậy, Mộ Tử Ch&acirc;n c&uacute;i đầu nh&igrave;n chằm chằm thanh trường kiếm trong tay. C&ograve;n nữ tử tay cầm b&aacute;n nguyệt k&iacute;ch kia th&igrave; chống c&aacute;n k&iacute;ch xuống đất, cao giọng n&oacute;i: &ldquo;Ta cũng biết Mặc Bạch ti&ecirc;n qu&acirc;n ng&agrave;i tu h&agrave;nh đắc đạo từ tấm th&acirc;n d&atilde; th&uacute;, tất nhi&ecirc;n kh&ocirc;ng vừa mắt chuyện bọn ta t&agrave;n s&aacute;t y&ecirc;u linh, c&ograve;n coi bọn ta l&agrave; những kẻ hiếu s&aacute;t tội &aacute;c t&agrave;y trời. Nhưng trong t&igrave;nh h&igrave;nh khi đ&oacute;, bọn ta c&ograve;n c&oacute; thể do dự được ư? Nếu kh&ocirc;ng tru diệt y&ecirc;u linh, đoạt lấy nội đan từ trong cơ thể bọn ch&uacute;ng, Thần Ch&acirc;u ắt kh&oacute; tr&aacute;nh khỏi cảnh bị nhấn ch&igrave;m, đến l&uacute;c đ&oacute; đừng h&ograve;ng ai mong sống s&oacute;t!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Những lời n&agrave;y của nữ tử khiến người của tứ đại m&ocirc;n ph&aacute;i nhao nhao gật đầu. Mặc Bạch nghe thấy vậy th&igrave; chỉ hơi nhếch kh&oacute;e m&ocirc;i, nở nụ cười giễu cợt, sau đ&oacute; lạnh l&ugrave;ng hỏi lại: &ldquo;Ứng Long v&agrave; Tương Liễu hẳn nhi&ecirc;n đ&atilde; bị phong ấn b&ecirc;n bờ Đ&ocirc;ng Hải từ mười năm trước, cớ g&igrave; m&agrave; từ đ&oacute; đến nay Tru Y&ecirc;u lệnh vẫn chẳng được r&uacute;t về, h&agrave;nh động t&agrave;n s&aacute;t vẫn k&eacute;o d&agrave;i kh&ocirc;ng dứt?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nữ tử đến từ Độ Tội cốc ngẩng cao đầu trả lời: &ldquo;Thu hồi Tru Y&ecirc;u lệnh th&igrave; c&oacute; kh&aacute;c n&agrave;o thả hổ về rừng. Lũ y&ecirc;u nghiệt vốn lu&ocirc;n g&acirc;y họa cho nh&acirc;n gian, sau khi bị tổn thất nặng nề ắt sẽ r&igrave;nh rập chờ thời cơ phản k&iacute;ch. Để tr&aacute;nh y&ecirc;u nghiệt ho&agrave;nh h&agrave;nh, g&acirc;y họa cho ch&uacute;ng sinh, cũng l&agrave; để bảo vệ l&ecirc; d&acirc;n thi&ecirc;n hạ, bọn ta chỉ c&oacute; c&aacute;ch giết sạch y&ecirc;u ma, như vậy mới c&oacute; thể mang lại sự b&igrave;nh y&ecirc;n vĩnh viễn!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Thực l&agrave; những lời hết mức qu&agrave;ng xi&ecirc;n!&rdquo; Mặc Bạch lạnh l&ugrave;ng đưa mắt nh&igrave;n nữ tử đ&oacute;, n&oacute;i. &ldquo;Đuổi tận giết tuyệt m&agrave; c&ograve;n n&oacute;i như l&agrave; bản th&acirc;n rất quang minh ch&iacute;nh đại, c&aacute;i g&igrave; m&agrave; &ldquo;bảo vệ l&ecirc; d&acirc;n thi&ecirc;n hạ&rdquo; chứ? N&oacute;i trắng ra l&agrave; sợ bị c&aacute;c y&ecirc;u linh m&agrave; c&aacute;c ngươi l&agrave;m hại trả th&ugrave; m&agrave; th&ocirc;i.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ngươi... ngươi chớ c&oacute; ăn n&oacute;i bậy bạ! T&agrave; ma ngoại đạo, ai ai cũng c&oacute; tr&aacute;ch nhiệm tru diệt! Độ Tội cốc ta l&ograve;ng mang ch&iacute;nh nghĩa, h&aacute; cho ph&eacute;p ngươi bu&ocirc;ng lời nhục mạ?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Tiếng tranh luận của nữ tử đ&oacute; vọng v&agrave;o trong ng&ocirc;i nh&agrave; tr&uacute;c, lọt v&agrave;o tai Nguyệt Tiểu Tr&uacute;c. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n n&agrave;ng được biết đằng sau Tru Y&ecirc;u lệnh c&ograve;n c&oacute; nội t&igrave;nh như thế, rồi n&agrave;ng bỗng nhớ lại rắn con ca ca m&agrave; m&igrave;nh từng gặp mười năm trước. Hồi đ&oacute;, y bị lo&agrave;i người đ&aacute;nh trọng thương, vốn mang đầy l&ograve;ng th&ugrave; hận, quả thực từng muốn giết sạch người đời, c&ograve;n định bẻ g&atilde;y cổ của n&agrave;ng, nhưng cũng ch&iacute;nh rắn con ca ca đ&atilde; ki&ecirc;n định đứng chắn trước mặt n&agrave;ng, gi&uacute;p n&agrave;ng chặn thanh trường kiếm của Mộ Tử Ch&acirc;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Đương khi Nguyệt Tiểu Tr&uacute;c nhớ lại chuyện xưa, t&igrave;nh h&igrave;nh b&ecirc;n ngo&agrave;i căn nh&agrave; tr&uacute;c lại ph&aacute;t sinh biến h&oacute;a. Nghe xong mấy lời của Tất Phi, Mặc Bạch đ&atilde; đo&aacute;n được đại kh&aacute;i &yacute; đồ của đ&aacute;m người n&agrave;y, b&egrave;n cất giọng lạnh l&ugrave;ng: &ldquo;Phong ấn b&ecirc;n bờ Đ&ocirc;ng Hải ắt c&oacute; chỗ sơ hở, thế n&ecirc;n c&aacute;c ngươi mới nảy sinh &yacute; đồ với ta như thế. C&oacute; điều phen n&agrave;y chỉ e c&aacute;c ngươi phải thất vọng rồi, ta chưa từng nghe n&oacute;i tới V&acirc;n Sinh k&iacute;nh.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;N&oacute;i bậy!&rdquo; Nữ tử kia gằn giọng qu&aacute;t. &ldquo;Sư t&ocirc;n<sup>1</sup>&nbsp;ta từng n&oacute;i r&otilde;, V&acirc;n Sinh k&iacute;nh c&oacute; c&ocirc;ng hiệu cải tử ho&agrave;n sinh, người ph&agrave;m m&agrave; c&oacute; được n&oacute;, chỉ cần tu luyện đ&uacute;ng c&aacute;ch th&igrave; chẳng tới mấy mươi năm đ&atilde; c&oacute; thể đắc đạo phi thăng. Mặc Bạch ngươi vốn chỉ l&agrave; lo&agrave;i d&atilde; th&uacute;, dương thọ liệu c&oacute; được bao nhi&ecirc;u? Nếu kh&ocirc;ng c&oacute; V&acirc;n Sinh k&iacute;nh trong tay, lẽ n&agrave;o ngươi c&oacute; thể dễ d&agrave;ng tu luyện th&agrave;nh địa ti&ecirc;n như thế? Đừng coi ta l&agrave; trẻ con ba tuổi m&agrave; bu&ocirc;ng lời lừa gạt!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">1</span></span></span></sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">. C&aacute;ch xưng h&ocirc; tỏ &yacute; k&iacute;nh trọng đối với sư phụ.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Lục c&ocirc; nương, xin đừng n&ocirc;n n&oacute;ng!&rdquo; Tất Phi cất lời ngăn cản Lục Linh, sau đ&oacute; lại quay sang nh&igrave;n Mặc Bạch, thấp giọng thỉnh cầu: &ldquo;Tiền bối, nếu kh&ocirc;ng v&igrave; t&igrave;nh thế nguy cấp, bọn v&atilde;n bối đ&atilde; chẳng tới đ&acirc;y quấy rầy. Ch&iacute;nh như tiền bối vừa n&oacute;i, phong ấn b&ecirc;n bờ Đ&ocirc;ng Hải đ&atilde; nguy ngập lắm rồi, Ứng Long v&agrave; Tương Liễu kh&ocirc;ng l&acirc;u sau sẽ ph&aacute; phong ấn tho&aacute;t ra, tới l&uacute;c đ&oacute; thảm trạng mười năm trước sẽ lại một lần nữa xảy đến. Tiền bối th&acirc;n l&agrave; ti&ecirc;n qu&acirc;n, lẽ n&agrave;o lại kh&ocirc;ng quan t&acirc;m tới việc n&agrave;y, thản nhi&ecirc;n sống cuộc sống thanh b&igrave;nh của m&igrave;nh tr&ecirc;n n&uacute;i, để mặc cho mu&ocirc;n vạn ch&uacute;ng sinh phải rơi v&agrave;o cảnh nước s&ocirc;i lửa bỏng hay sao?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Lời của Tất Phi khiến Mặc Bạch bất gi&aacute;c trầm ng&acirc;m một l&aacute;t, rồi y chậm r&atilde;i n&oacute;i: &ldquo;Trời cao lồng lộng, tự c&oacute; an b&agrave;i. Kh&ocirc;ng phải ta cố t&igrave;nh khoanh tay đứng nh&igrave;n, thật sự l&agrave; trong tay ta kh&ocirc;ng c&oacute; vật m&agrave; c&aacute;c ngươi đang t&igrave;m kiếm.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mộ Tử Ch&acirc;n v&agrave; Tất Phi ngơ ng&aacute;c nh&igrave;n nhau, suốt hồi l&acirc;u chẳng n&oacute;i năng g&igrave;. Đ&aacute;m người của Độ Tội cốc th&igrave; đ&atilde; bắt đầu xao động, g&atilde; h&aacute;n tử vừa rồi k&ecirc;u to: &ldquo;Đừng nghe hắn n&oacute;i linh tinh! Ph&aacute;n đo&aacute;n của sư phụ ta chưa bao giờ sai, V&acirc;n Sinh k&iacute;nh nhất định nằm trong tay kẻ n&agrave;y! C&aacute;c huynh đệ, ra tay th&ocirc;i!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Dứt lời, hắn qu&aacute;t to một tiếng, tung m&igrave;nh nhảy vọt l&ecirc;n, thanh trường k&iacute;ch m&agrave;u trắng bạc trong tay ch&eacute;m thẳng về ph&iacute;a đỉnh đầu Mặc Bạch. Mặc Bạch khẽ xoay người một ch&uacute;t, đầu ng&oacute;n tay phất nhẹ, lập tức c&oacute; một chiếc l&aacute; tr&uacute;c xanh biếc lao thẳng về ph&iacute;a bả vai của đối phương, cắm s&acirc;u v&agrave;o trong thịt, tuy kh&ocirc;ng đến nỗi khiến kẻ n&agrave;y bị trọng thương nhưng cũng l&agrave;m cho vai hắn t&ecirc; rần, kh&ocirc;ng thể n&agrave;o nhấc thanh trường k&iacute;ch nặng nề kia l&ecirc;n được nữa.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Lui xuống!&rdquo; Sau một tiếng qu&aacute;t lớn, Lục Linh vung c&acirc;y b&aacute;n nguyệt k&iacute;ch của m&igrave;nh l&ecirc;n, nhắm thẳng hướng Mặc Bạch m&agrave; bổ tới, mấy g&atilde; m&ocirc;n nh&acirc;n c&ograve;n lại của Độ Tội cốc cũng lập tức ra tay. Tức th&igrave;, &aacute;nh bạc bừng l&ecirc;n lấp l&aacute;nh, gi&oacute; lớn nổi l&ecirc;n v&ugrave; v&ugrave;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Mặc Bạch trầm hẳn sắc mặt, tay phải r&uacute;t c&acirc;y gậy tr&uacute;c xanh c&agrave;i ở b&ecirc;n h&ocirc;ng, vung l&ecirc;n chặn thanh b&aacute;n nguyệt k&iacute;ch kia lại, c&ugrave;ng l&uacute;c đ&oacute;, tay tr&aacute;i y bắt quyết tạo ra một cơn gi&oacute; lốc. Đột nhi&ecirc;n, tr&ecirc;n đỉnh n&uacute;i gi&oacute; m&acirc;y biến sắc, giữa trời đất đột nhi&ecirc;n xuất hiện một c&aacute;i b&oacute;ng lớn tựa như con rồng khổng lồ, rừng tr&uacute;c x&agrave;o xạc dữ dội, v&ocirc; số chiếc l&aacute; tr&uacute;c bị cuốn v&agrave;o cơn lốc, xoay tr&ograve;n kh&ocirc;ng ngớt, qu&eacute;t qua khắp cả ngọn n&uacute;i.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Những chiếc l&aacute; tr&uacute;c thường ng&agrave;y vốn xanh biếc hiền h&ograve;a, ấy vậy m&agrave; l&uacute;c n&agrave;y lại giống như v&ocirc; v&agrave;n lưỡi dao sắc b&eacute;n lao đi vun v&uacute;t b&ecirc;n cạnh mọi người. M&aacute; tr&aacute;i của Lục Linh bị l&aacute; tr&uacute;c qu&eacute;t qua một đường, lập tức lưu lại một vết m&aacute;u rất d&agrave;i. C&ocirc; ta phẫn nộ h&eacute;t lớn, dốc hết sức m&igrave;nh vung trường k&iacute;ch đ&acirc;m thẳng về ph&iacute;a Mặc Bạch. C&ugrave;ng l&uacute;c đ&oacute;, đệ tử của ba ph&aacute;i kh&aacute;c thấy người của Độ Tội cốc gặp kh&oacute; khăn, nhất tề nhắm thẳng hướng Mặc Bạch m&agrave; xuất thủ.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Người của X&iacute;ch V&acirc;n l&acirc;u lập tức m&oacute;c ra những tấm b&ugrave;a, ngo&agrave;i Tất Phi th&igrave; s&aacute;u người c&ograve;n lại đều x&ocirc;ng tới v&acirc;y Mặc Bạch v&agrave;o giữa, sau đ&oacute; kẹp Phược Gi&aacute;p ph&ugrave;<sup>1</sup>&nbsp;giữa hai ng&oacute;n tay, d&ugrave;ng sức n&eacute;m về ph&iacute;a Mặc Bạch, đồng thời cất tiếng h&ocirc; to: &ldquo;Phong!&rdquo; S&aacute;u l&aacute; b&ugrave;a tựa như s&aacute;u mũi t&ecirc;n vừa rời khỏi d&acirc;y, đồng loạt ch&aacute;y b&ugrave;ng giữa kh&ocirc;ng trung v&agrave; lao thẳng về ph&iacute;a Mặc Bạch. Mặc Bạch vung tay phải một c&aacute;i, d&ugrave;ng c&acirc;y gậy tr&uacute;c qu&eacute;t bay một l&aacute; b&ugrave;a đi, nhưng đ&uacute;ng v&agrave;o l&uacute;c n&agrave;y, Mộ Tử Ch&acirc;n đ&atilde; dẫn theo m&ocirc;n nh&acirc;n của Thi&ecirc;n Huyền m&ocirc;n gia nhập v&agrave;o v&ograve;ng chiến.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">1</span></span></span></sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">. Ph&ugrave; tức l&agrave; b&ugrave;a.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ti&ecirc;n qu&acirc;n, xin đắc tội!&rdquo; Mộ Tử Ch&acirc;n trầm giọng n&oacute;i, sau đ&oacute; liền cất giọng sang sảng h&ocirc; to: &ldquo;Tứ Tượng Lục Hợp, kiếm trận, khởi!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Lập tức, s&aacute;u g&atilde; kiếm sĩ nhất tề ra tay. Bốn người trong số đ&oacute; đứng về bốn hướng, hai người c&ograve;n lại th&igrave; một người bay l&ecirc;n tấn c&ocirc;ng đỉnh đầu Mặc Bạch, người c&ograve;n lại tập trung tấn c&ocirc;ng v&agrave;o hạ b&agrave;n. Kiếm trận n&agrave;y v&ocirc; c&ugrave;ng k&iacute;n kẽ, ho&agrave;n to&agrave;n chặn đứng đường lui của Mặc</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Bạch, m&agrave; bốn g&atilde; kiếm sĩ ở bốn hướng đ&ocirc;ng, t&acirc;y, nam, bắc c&ograve;n c&oacute; thể phối hợp với Tứ Tượng trận vốn đang phong tỏa ngọn n&uacute;i n&agrave;y, tạo ra một tấm lưới lớn giữa trời đất. Mặc Bạch cầm gậy tr&uacute;c trong tay, nhắm v&agrave;o g&atilde; kiếm sĩ ở hướng nam m&agrave; đ&aacute;nh tới, muốn mở ra một kẽ hở, n&agrave;o ngờ Mộ Tử Ch&acirc;n lại nhảy v&agrave;o giữa trận, dồn hết nội k&igrave;nh v&agrave;o thanh trường kiếm v&agrave; quấn lấy c&acirc;y gậy tr&uacute;c của Mặc Bạch, kh&ocirc;ng cho y c&oacute; cơ hội ph&aacute; trận.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ph&aacute;!&rdquo; Mộ Tử Ch&acirc;n qu&aacute;t lớn một tiếng, s&aacute;u g&atilde; kiếm sĩ lập tức hướng l&ograve;ng b&agrave;n tay phải l&ecirc;n trời, trường kiếm tr&ecirc;n tay vẫn xoay tr&ograve;n kh&ocirc;ng ngớt, tốc độ nhanh tới nỗi khiến người ta kh&ocirc;ng mở mắt ra nổi. M&ocirc;n nh&acirc;n của X&iacute;ch V&acirc;n l&acirc;u thừa dịp n&agrave;y lại một lần nữa ph&oacute;ng ra Phược Gi&aacute;p ph&ugrave;. Mặc Bạch vừa định d&ugrave;ng gi&oacute; lốc ngăn cản th&igrave; chợt cảm thấy ch&acirc;n kh&iacute; trong lồng ngực tắc nghẽn, chẳng thể sử ra ch&uacute;t ph&aacute;p thuật n&agrave;o.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Ngươi tuy l&agrave; ti&ecirc;n qu&acirc;n nhưng cũng chớ n&ecirc;n coi thường thủ đoạn của người ph&agrave;m bọn ta.&rdquo; Chợt nghe một giọng nam &acirc;m nhu vang l&ecirc;n k&egrave;m theo tiếng cười khẽ. Người n&agrave;y ch&iacute;nh l&agrave; Lận Bạch Trạch, thủ lĩnh của đ&aacute;m người Thập Phương điện. H&oacute;a ra vừa rồi khi Mặc Bạch d&ugrave;ng gi&oacute; lốc để cự địch, Lận Bạch Trạch đ&atilde; thừa dịp lấy ra loại một dược vật chuy&ecirc;n d&ugrave;ng để khắc chế ti&ecirc;n linh l&agrave; bột của Thị Kh&ocirc; đằng<sup>1</sup>, sau đ&oacute; t&igrave;m cơ hội rắc v&agrave;o trong gi&oacute;.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">1</span></span></span></sup><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">. Đằng tức l&agrave; c&acirc;y m&acirc;y.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Ch&acirc;n kh&iacute; bị tắc nghẽn, kh&ocirc;ng thể sử dụng ph&aacute;p thuật, Mặc Bạch đ&agrave;nh giao đấu với đối phương bằng chi&ecirc;u thức v&otilde; c&ocirc;ng. Người của Độ Tội cốc lập tức &ugrave;a l&ecirc;n, to&agrave;n ra chi&ecirc;u triệt hạ. Mặc Bạch di chuyển mau lẹ như gi&oacute;, nhưng vừa mới n&eacute; được một chi&ecirc;u thức dữ dội của Lục Linh th&igrave; ph&iacute;a b&ecirc;n kia, Phược Gi&aacute;p ph&ugrave; của m&ocirc;n nh&acirc;n X&iacute;ch V&acirc;n l&acirc;u đ&atilde; bay tới. L&aacute; b&ugrave;a nổ tung v&agrave; đ&aacute;nh tr&uacute;ng v&agrave;o giữa lưng Mặc Bạch, khiến y loạng choạng ng&atilde; nh&agrave;o về ph&iacute;a trước.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Lục Linh thấy thế th&igrave; lại tiếp tục tung s&aacute;t chi&ecirc;u, sau khi xoay người một v&ograve;ng giữa kh&ocirc;ng trung liền vung trường k&iacute;ch đ&acirc;m thẳng về ph&iacute;a lưng Mặc Bạch. Đ&uacute;ng v&agrave;o khoảnh khắc mũi k&iacute;ch sắc b&eacute;n chuẩn bị đ&acirc;m ngập v&agrave;o lưng người thanh ni&ecirc;n, tiếng gi&oacute; bỗng r&iacute;t l&ecirc;n dữ dội, một sợi d&acirc;y d&agrave;i xanh biếc chợt quấn lấy c&aacute;n k&iacute;ch, khiến cho đ&ograve;n tấn c&ocirc;ng của Lục Linh trật hướng. Lục Linh cả giận, khi ngước mắt l&ecirc;n th&igrave; thấy từ trong ng&ocirc;i nh&agrave; tr&uacute;c c&oacute; một b&oacute;ng d&aacute;ng nhanh nhẹn tựa chim &eacute;n bay ra, sau nh&aacute;y mắt đ&atilde; tới b&ecirc;n cạnh Mặc Bạch.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Kh&ocirc;ng được đả thương sư phụ ta! Lũ c&aacute;c ngươi lu&ocirc;n mồm n&oacute;i đến ch&iacute;nh nghĩa, thế m&agrave; khi mượn đồ kh&ocirc;ng được lại b&agrave;y tr&ograve; cướp đoạt, ch&iacute;nh nghĩa của c&aacute;c ngươi l&agrave; như vậy ư?&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Giọng n&oacute;i của c&ocirc; nương đ&oacute; ngọt ng&agrave;o, &ecirc;m tai, nhưng lời lẽ to&aacute;t ra lại v&ocirc; c&ugrave;ng ki&ecirc;n định.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nghe thấy những lời n&agrave;y của n&agrave;ng, Tất Phi c&uacute;i đầu trầm tư, thần sắc lộ r&otilde; vẻ ảm đạm. Nhưng g&atilde; h&aacute;n tử đến từ Độ Tội cốc kia th&igrave; lại nhếch m&ocirc;i cười d&acirc;m dật, n&oacute;i: &ldquo;Quả đ&uacute;ng l&agrave; d&atilde; th&uacute; th&agrave;nh ti&ecirc;n, c&ograve;n nu&ocirc;i một tiểu t&igrave;nh nh&acirc;n trong nh&agrave; nữa.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Nghe thấy những lời n&agrave;y của hắn, sắc mặt Mặc Bạch trầm hẳn xuống, trong mắt bừng l&ecirc;n những tia băng gi&aacute; chưa từng c&oacute;. Y lập tức vung tay, một chiếc l&aacute; tr&uacute;c liền x&eacute; gi&oacute; bay vụt tới, tựa như một lưỡi dao cắm v&agrave;o m&ocirc;i dưới của g&atilde; h&aacute;n tử vừa cất lời xằng bậy, sau đ&oacute; xuy&ecirc;n ra ngo&agrave;i qua m&ocirc;i tr&ecirc;n của hắn. Mặc Bạch cất giọng lạnh l&ugrave;ng: &ldquo;C&aacute;i miệng chỉ biết n&oacute;i năng xằng bậy n&agrave;y n&ecirc;n kh&acirc;u lại l&agrave; hơn.&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">G&atilde; h&aacute;n tử đ&oacute; đau đến nỗi kh&oacute;e miệng co giật, vậy nhưng chẳng thể n&agrave;o mở miệng để k&ecirc;u l&ecirc;n một tiếng, chỉ c&oacute; thể r&ecirc;n rỉ kh&ocirc;ng ngớt. Thấy thế, đ&aacute;m người c&ograve;n lại của bốn m&ocirc;n ph&aacute;i lại c&agrave;ng tấn c&ocirc;ng m&atilde;nh liệt, Mộ Tử Ch&acirc;n th&igrave; chỉ huy c&aacute;c m&ocirc;n nh&acirc;n của Thi&ecirc;n Huyền m&ocirc;n d&ugrave;ng trường kiếm t&aacute;i lập Tứ Tượng Lục Hợp kiếm trận. L&uacute;c n&agrave;y, Mặc Bạch đ&atilde; kh&ocirc;ng thể sử dụng ph&aacute;p thuật để ngăn cản, Nguyệt Tiểu Tr&uacute;c lập tức d&ugrave;ng d&acirc;y tấn c&ocirc;ng, muốn đ&aacute;nh rớt một thanh kiếm trong số đ&oacute; để ph&aacute; kiếm trận. Nhưng &yacute; đồ của n&agrave;ng sớm đ&atilde; bị Mộ Tử Ch&acirc;n nh&igrave;n thấu, hắn lật cổ tay một c&aacute;i, trường kiếm đ&acirc;m ra, dễ d&agrave;ng chặn sợi x&iacute;ch của Tiểu Tr&uacute;c lại.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">&ldquo;Vạn Tượng Quy Nguy&ecirc;n, thu!&rdquo;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;bookerly&quot;,serif\"><span style=\"color:black\">Sau một tiếng qu&aacute;t lớn của Mộ Tử Ch&acirc;n, Tứ Tượng Lục Hợp kiếm trận tỏa ra những tia s&aacute;ng v&ocirc; c&ugrave;ng ch&oacute;i mắt, vẽ ra giữa kh&ocirc;ng trung h&igrave;nh một ng&ocirc;i sao lục gi&aacute;c, m&agrave; vị tr&iacute; của Mặc Bạch ch&iacute;nh l&agrave; ở giữa ng&ocirc;i sao đ&oacute;. &Aacute;nh s&aacute;ng nhanh ch&oacute;ng co r&uacute;t lại, cuối c&ugrave;ng h&oacute;a th&agrav');

